import { useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { ArrowRight, Shield } from "lucide-react";
import heroImage from "@assets/generated_images/Futuristic_vault_with_energy_streams_c11dc6a9.png";
import AnimatedBackground from "./AnimatedBackground";
import gsap from "gsap";

interface VaultHeroProps {
  onGetStarted?: () => void;
  onLearnMore?: () => void;
}

export default function VaultHero({ onGetStarted, onLearnMore }: VaultHeroProps) {
  const heroRef = useRef<HTMLDivElement>(null);
  const imageRef = useRef<HTMLDivElement>(null);
  const textRef = useRef<HTMLDivElement>(null);
  const statsRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const tl = gsap.timeline({ defaults: { ease: "power3.out" } });

    tl.fromTo(
      textRef.current,
      { opacity: 0, y: 60 },
      { opacity: 1, y: 0, duration: 1 }
    )
      .fromTo(
        imageRef.current,
        { opacity: 0, scale: 0.9, rotate: -5 },
        { opacity: 1, scale: 1, rotate: 0, duration: 1.2 },
        "-=0.7"
      )
      .fromTo(
        statsRef.current?.children || [],
        { opacity: 0, y: 30 },
        { opacity: 1, y: 0, duration: 0.6, stagger: 0.15 },
        "-=0.5"
      );

    if (imageRef.current) {
      gsap.to(imageRef.current, {
        y: -20,
        duration: 3,
        repeat: -1,
        yoyo: true,
        ease: "sine.inOut",
      });
    }
  }, []);

  return (
    <section className="relative min-h-[85vh] flex items-center gradient-bg-vault overflow-hidden">
      <AnimatedBackground />

      <div className="absolute inset-0 bg-[linear-gradient(to_right,hsl(var(--border))_1px,transparent_1px),linear-gradient(to_bottom,hsl(var(--border))_1px,transparent_1px)] bg-[size:4rem_4rem] opacity-20" />

      <div className="absolute inset-0 bg-gradient-to-b from-transparent via-transparent to-background/80" />

      <div className="container mx-auto px-6 relative z-10">
        <div className="grid lg:grid-cols-2 gap-12 items-center">
          <div ref={textRef} className="space-y-8">
            <div className="inline-flex items-center gap-2 px-4 py-2 rounded-full bg-primary/10 border border-primary/20 text-primary text-sm font-medium backdrop-blur-sm">
              <Shield className="w-4 h-4" />
              <span>Zero-Custody Architecture</span>
            </div>

            <h1 className="text-6xl lg:text-7xl font-bold font-display leading-tight">
              <span className="gradient-text-primary">Secure</span> Your Crypto
              <br />
              Legacy Forever
            </h1>

            <p className="text-lg text-muted-foreground max-w-xl">
              The only dead man's switch that guarantees safe inheritance of
              self-custodied crypto assetsâ€”without ever surrendering control of
              your private keys while you're alive.
            </p>

            <div className="flex flex-wrap gap-4">
              <Button
                size="lg"
                className="text-base px-8 glow-primary group"
                onClick={onGetStarted}
                data-testid="button-get-started"
              >
                Get Started
                <ArrowRight className="ml-2 w-5 h-5 transition-transform group-hover:translate-x-1" />
              </Button>
              <Button
                size="lg"
                variant="outline"
                className="text-base px-8 backdrop-blur-sm"
                onClick={onLearnMore}
                data-testid="button-learn-more"
              >
                Learn More
              </Button>
            </div>

            <div ref={statsRef} className="flex items-center gap-8 pt-4">
              <div>
                <div className="text-3xl font-bold font-display gradient-text-primary">$200B+</div>
                <div className="text-sm text-muted-foreground">Lost Crypto Assets</div>
              </div>
              <div className="h-12 w-px bg-border" />
              <div>
                <div className="text-3xl font-bold font-display gradient-text-primary">256-bit</div>
                <div className="text-sm text-muted-foreground">Encryption</div>
              </div>
              <div className="h-12 w-px bg-border" />
              <div>
                <div className="text-3xl font-bold font-display gradient-text-primary">3-of-5</div>
                <div className="text-sm text-muted-foreground">Multi-Party Recovery</div>
              </div>
            </div>
          </div>

          <div ref={imageRef} className="relative">
            <div className="absolute inset-0 bg-gradient-to-tr from-primary/20 via-transparent to-accent/20 rounded-2xl blur-3xl" />
            <div className="relative rounded-2xl overflow-hidden glow-primary">
              <img
                src={heroImage}
                alt="Futuristic vault with glowing energy streams"
                className="w-full h-auto object-cover"
              />
            </div>
            <div className="absolute -top-4 -right-4 w-24 h-24 bg-primary/20 rounded-full blur-2xl animate-pulse" />
            <div className="absolute -bottom-4 -left-4 w-32 h-32 bg-accent/20 rounded-full blur-2xl animate-pulse" style={{ animationDelay: "1s" }} />
          </div>
        </div>
      </div>
    </section>
  );
}

client/src/components/WizardProgress.tsx.content:
import { Check } from "lucide-react";

interface WizardStep {
  id: number;
  title: string;
  description: string;
}

interface WizardProgressProps {
  steps: WizardStep[];
  currentStep: number;
}

export default function WizardProgress({
  steps,
  currentStep,
}: WizardProgressProps) {
  return (
    <div className="w-full" data-testid="wizard-progress">
      <div className="flex items-center justify-between">
        {steps.map((step, index) => {
          const isCompleted = index < currentStep;
          const isCurrent = index === currentStep;
          const isUpcoming = index > currentStep;

          return (
            <div key={step.id} className="flex items-center flex-1">
              <div className="flex flex-col items-center">
                <div
                  className={`
                    w-12 h-12 rounded-full flex items-center justify-center font-semibold text-sm
                    transition-all duration-300
                    ${
                      isCompleted
                        ? "bg-primary text-primary-foreground glow-primary"
                        : isCurrent
                        ? "bg-primary/20 text-primary border-2 border-primary glow-primary"
                        : "bg-muted text-muted-foreground"
                    }
                  `}
                  data-testid={`step-${step.id}`}
                >
                  {isCompleted ? (
                    <Check className="w-6 h-6" />
                  ) : (
                    <span>{step.id}</span>
                  )}
                </div>
                <div className="mt-3 text-center">
                  <div
                    className={`text-sm font-medium ${
                      isCurrent
                        ? "text-foreground"
                        : isCompleted
                        ? "text-primary"
                        : "text-muted-foreground"
                    }`}
                  >
                    {step.title}
                  </div>
                  <div className="text-xs text-muted-foreground mt-1 max-w-[120px]">
                    {step.description}
                  </div>
                </div>
              </div>
              {index < steps.length - 1 && (
                <div className="flex-1 h-0.5 mx-4 mb-12">
                  <div
                    className={`h-full transition-all duration-500 ${
                      isCompleted ? "bg-primary" : "bg-border"
                    }`}
                  />
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}

