import { useRef, useEffect } from "react";
import guardianImage from "@assets/generated_images/Guardian_network_distribution_diagram_a6648383.png";
import timelockImage from "@assets/generated_images/Time-lock_hourglass_blockchain_illustration_5b856fc2.png";
import securityImage from "@assets/generated_images/Layered_security_shield_illustration_0d99c804.png";
import { useFadeInUp, useScaleIn } from "@/hooks/useGsapScroll";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

interface Feature {
  title: string;
  description: string;
  image: string;
  features: string[];
}

const features: Feature[] = [
  {
    title: "Shamir Secret Sharing",
    description:
      "Your private key is split into 5 encrypted fragments distributed to trusted guardians. Only 3 fragments are needed for recovery, ensuring redundancy and security.",
    image: guardianImage,
    features: [
      "5 encrypted key fragments",
      "3-of-5 threshold for recovery",
      "Military-grade encryption",
      "Zero-knowledge architecture",
    ],
  },
  {
    title: "Time-Locked Protection",
    description:
      "Fragments only decrypt after missed check-ins plus a grace period. You maintain complete control with regular proof-of-life confirmations.",
    image: timelockImage,
    features: [
      "Customizable check-in schedule",
      "Multi-day grace period",
      "Email & SMS reminders",
      "Emergency override option",
    ],
  },
  {
    title: "Multi-Party Verification",
    description:
      "Death verification requires attestor consensus using verifiable proofs. Your beneficiaries are automatically notified when the process begins.",
    image: securityImage,
    features: [
      "Legal attestor network",
      "NFT death certificates",
      "Cryptographic proof system",
      "Progressive inheritance release",
    ],
  },
];

function FeatureCard({ feature, index }: { feature: Feature; index: number }) {
  const cardRef = useRef<HTMLDivElement>(null);
  const imageRef = useRef<HTMLDivElement>(null);
  const listRef = useRef<HTMLUListElement>(null);

  useEffect(() => {
    if (!cardRef.current || !imageRef.current || !listRef.current) return;

    const isReverse = index % 2 === 1;

    gsap.fromTo(
      cardRef.current,
      {
        opacity: 0,
        x: isReverse ? 100 : -100,
      },
      {
        opacity: 1,
        x: 0,
        duration: 1,
        ease: "power3.out",
        scrollTrigger: {
          trigger: cardRef.current,
          start: "top 80%",
          toggleActions: "play none none reverse",
        },
      }
    );

    gsap.fromTo(
      imageRef.current,
      {
        opacity: 0,
        scale: 0.8,
        rotate: isReverse ? 5 : -5,
      },
      {
        opacity: 1,
        scale: 1,
        rotate: 0,
        duration: 1.2,
        ease: "back.out(1.2)",
        scrollTrigger: {
          trigger: imageRef.current,
          start: "top 80%",
          toggleActions: "play none none reverse",
        },
      }
    );

    gsap.fromTo(
      listRef.current.children,
      {
        opacity: 0,
        x: -20,
      },
      {
        opacity: 1,
        x: 0,
        duration: 0.6,
        stagger: 0.1,
        ease: "power2.out",
        scrollTrigger: {
          trigger: listRef.current,
          start: "top 85%",
          toggleActions: "play none none reverse",
        },
      }
    );
  }, [index]);

  return (
    <div
      ref={cardRef}
      className={`grid lg:grid-cols-2 gap-12 items-center ${
        index % 2 === 1 ? "lg:flex-row-reverse" : ""
      }`}
    >
      <div className={index % 2 === 1 ? "lg:order-2" : ""}>
        <h3 className="text-4xl font-bold font-display mb-4 gradient-text-primary">
          {feature.title}
        </h3>
        <p className="text-lg text-muted-foreground mb-6">
          {feature.description}
        </p>
        <ul ref={listRef} className="space-y-3">
          {feature.features.map((item) => (
            <li key={item} className="flex items-center gap-3">
              <div className="w-2 h-2 rounded-full bg-primary glow-primary" />
              <span className="text-foreground">{item}</span>
            </li>
          ))}
        </ul>
      </div>
      <div className={index % 2 === 1 ? "lg:order-1" : ""}>
        <div
          ref={imageRef}
          className="relative rounded-2xl overflow-hidden bg-card border border-card-border p-8 hover-elevate group cursor-pointer"
        >
          <div className="absolute inset-0 bg-gradient-to-br from-primary/5 via-transparent to-accent/5 opacity-0 group-hover:opacity-100 transition-opacity duration-500" />
          <img
            src={feature.image}
            alt={feature.title}
            className="w-full h-auto object-contain relative z-10 transition-transform duration-500 group-hover:scale-105"
          />
        </div>
      </div>
    </div>
  );
}

export default function FeatureSection() {
  const titleRef = useFadeInUp();

  return (
    <section className="py-24 bg-background relative overflow-hidden" id="features">
      <div className="absolute inset-0 bg-gradient-to-b from-transparent via-primary/5 to-transparent" />

      <div className="container mx-auto px-6 relative z-10">
        <div ref={titleRef} className="text-center mb-16">
          <h2 className="text-5xl font-bold font-display mb-4">
            How It Works
          </h2>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
            Four cryptographic primitives combined into one unbreakable protocol
          </p>
        </div>

        <div className="space-y-24">
          {features.map((feature, index) => (
            <FeatureCard key={feature.title} feature={feature} index={index} />
          ))}
        </div>
      </div>
    </section>
  );
}

