const steps = [
  { id: 1, title: "Connect Wallet", description: "Link your crypto wallet" },
  { id: 2, title: "Add Guardians", description: "Select trusted guardians" },
  { id: 3, title: "Set Beneficiaries", description: "Define inheritors" },
