import { Card } from "@/components/ui/card";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Mail, MoreVertical } from "lucide-react";

interface GuardianCardProps {
  name: string;
  email: string;
  role: "guardian" | "beneficiary" | "attestor";
  status: "active" | "pending" | "declined";
  fragmentId?: string;
  onAction?: () => void;
}

export default function GuardianCard({
  name,
  email,
  role,
  status,
  fragmentId,
  onAction,
}: GuardianCardProps) {
  const initials = name
    .split(" ")
    .map((n) => n[0])
    .join("")
    .toUpperCase();

  const statusColors = {
    active: "bg-status-online/10 text-status-online border-status-online/20",
    pending: "bg-status-away/10 text-status-away border-status-away/20",
    declined: "bg-status-busy/10 text-status-busy border-status-busy/20",
  };

  const roleColors = {
    guardian: "bg-primary/10 text-primary border-primary/20",
    beneficiary: "bg-chart-2/10 text-chart-2 border-chart-2/20",
    attestor: "bg-chart-5/10 text-chart-5 border-chart-5/20",
  };

  return (
    <Card className="p-4 hover-elevate" data-testid={`card-${role}-${name.toLowerCase().replace(/\s+/g, "-")}`}>
      <div className="flex items-center gap-4">
        <Avatar className="h-12 w-12">
          <AvatarFallback className="bg-primary/20 text-primary font-semibold">
            {initials}
          </AvatarFallback>
        </Avatar>

        <div className="flex-1 min-w-0">
          <div className="flex items-center gap-2 mb-1">
            <h4 className="font-semibold truncate" data-testid={`text-name-${name.toLowerCase().replace(/\s+/g, "-")}`}>{name}</h4>
            <Badge
              variant="outline"
              className={`${roleColors[role]} text-xs uppercase`}
              data-testid={`badge-role-${role}`}
            >
              {role}
            </Badge>
          </div>
          <div className="flex items-center gap-2 text-sm text-muted-foreground">
            <Mail className="w-3 h-3" />
            <span className="truncate">{email}</span>
          </div>
          {fragmentId && (
            <div className="mt-1">
              <code className="text-xs font-mono text-muted-foreground bg-muted px-2 py-1 rounded">
                {fragmentId}
              </code>
            </div>
          )}
        </div>

        <div className="flex items-center gap-2">
          <Badge
            variant="outline"
            className={`${statusColors[status]} text-xs uppercase`}
            data-testid={`badge-status-${status}`}
          >
            {status}
          </Badge>
          <Button
            size="icon"
            variant="ghost"
            onClick={onAction}
            data-testid={`button-action-${name.toLowerCase().replace(/\s+/g, "-")}`}
          >
            <MoreVertical className="w-4 h-4" />
          </Button>
        </div>
      </div>
    </Card>
  );
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import {
  pgTable,
  text,
  varchar,
  timestamp,
  integer,
  boolean,
  pgEnum,
} from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Users table - wallet-based authentication
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  walletAddress: text("wallet_address").notNull().unique(),
  createdAt: timestamp("created_at").notNull().defaultNow(),
  lastLoginAt: timestamp("last_login_at"),
});

// Vault status enum
export const vaultStatusEnum = pgEnum("vault_status", [
  "active",
  "warning",
  "critical",
  "triggered",
  "cancelled",
]);

// Vaults table - main vault configuration
export const vaults = pgTable("vaults", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  ownerId: varchar("owner_id")
    .notNull()
    .references(() => users.id, { onDelete: "cascade" }),
  name: text("name").notNull(),
  checkInIntervalDays: integer("check_in_interval_days").notNull().default(90),
  gracePeriodDays: integer("grace_period_days").notNull().default(14),
  status: vaultStatusEnum("status").notNull().default("active"),
  lastCheckInAt: timestamp("last_check_in_at").notNull().defaultNow(),
  nextCheckInDue: timestamp("next_check_in_due").notNull(),
  createdAt: timestamp("created_at").notNull().defaultNow(),
  updatedAt: timestamp("updated_at").notNull().defaultNow(),
});

// Party role enum
export const partyRoleEnum = pgEnum("party_role", [
  "guardian",
  "beneficiary",
  "attestor",
]);

// Party status enum
export const partyStatusEnum = pgEnum("party_status", [
  "pending",
  "active",
  "declined",
  "inactive",
]);

// Parties table - guardians, beneficiaries, attestors
export const parties = pgTable("parties", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  vaultId: varchar("vault_id")
    .notNull()
    .references(() => vaults.id, { onDelete: "cascade" }),
  role: partyRoleEnum("role").notNull(),
  name: text("name").notNull(),
  email: text("email").notNull(),
  phone: text("phone"),
  status: partyStatusEnum("status").notNull().default("pending"),
  invitedAt: timestamp("invited_at").notNull().defaultNow(),
  acceptedAt: timestamp("accepted_at"),
  createdAt: timestamp("created_at").notNull().defaultNow(),
});

// Fragments table - encrypted key fragments
export const fragments = pgTable("fragments", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  vaultId: varchar("vault_id")
    .notNull()
    .references(() => vaults.id, { onDelete: "cascade" }),
  guardianId: varchar("guardian_id")
    .notNull()
    .references(() => parties.id, { onDelete: "cascade" }),
  fragmentIndex: integer("fragment_index").notNull(),
  encryptedData: text("encrypted_data").notNull(),
  createdAt: timestamp("created_at").notNull().defaultNow(),
});

// Check-ins table - history of vault check-ins
export const checkIns = pgTable("check_ins", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  vaultId: varchar("vault_id")
    .notNull()
    .references(() => vaults.id, { onDelete: "cascade" }),
  checkedInAt: timestamp("checked_in_at").notNull().defaultNow(),
  signature: text("signature").notNull(),
  ipAddress: text("ip_address"),
});

// Notification type enum
export const notificationTypeEnum = pgEnum("notification_type", [
  "check_in_reminder",
  "check_in_warning",
  "check_in_critical",
  "guardian_invitation",
  "beneficiary_notification",
  "attestor_request",
]);

// Notification status enum
export const notificationStatusEnum = pgEnum("notification_status", [
  "pending",
  "sent",
  "failed",
]);

// Notifications table - email/SMS queue
export const notifications = pgTable("notifications", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  vaultId: varchar("vault_id")
    .notNull()
    .references(() => vaults.id, { onDelete: "cascade" }),
  type: notificationTypeEnum("type").notNull(),
  recipient: text("recipient").notNull(),
  channel: text("channel").notNull(), // 'email' or 'sms'
  status: notificationStatusEnum("status").notNull().default("pending"),
  message: text("message").notNull(),
  sentAt: timestamp("sent_at"),
  createdAt: timestamp("created_at").notNull().defaultNow(),
});

// Insert schemas with Zod validation
export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
  lastLoginAt: true,
});

export const insertVaultSchema = createInsertSchema(vaults).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
  lastCheckInAt: true,
  nextCheckInDue: true,
  status: true,
});

export const insertPartySchema = createInsertSchema(parties).omit({
  id: true,
  createdAt: true,
  invitedAt: true,
  acceptedAt: true,
  status: true,
});

export const insertFragmentSchema = createInsertSchema(fragments).omit({
  id: true,
  createdAt: true,
});

export const insertCheckInSchema = createInsertSchema(checkIns).omit({
  id: true,
  checkedInAt: true,
});

export const insertNotificationSchema = createInsertSchema(notifications).omit({
  id: true,
  createdAt: true,
  sentAt: true,
  status: true,
});

// Type exports
export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

export type InsertVault = z.infer<typeof insertVaultSchema>;
export type Vault = typeof vaults.$inferSelect;

export type InsertParty = z.infer<typeof insertPartySchema>;
export type Party = typeof parties.$inferSelect;

export type InsertFragment = z.infer<typeof insertFragmentSchema>;
export type Fragment = typeof fragments.$inferSelect;

export type InsertCheckIn = z.infer<typeof insertCheckInSchema>;
export type CheckIn = typeof checkIns.$inferSelect;

export type InsertNotification = z.infer<typeof insertNotificationSchema>;
export type Notification = typeof notifications.$inferSelect;

