import { useRef, useEffect } from "react";
import PricingCard from "./PricingCard";
import { useFadeInUp } from "@/hooks/useGsapScroll";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

interface PricingSectionProps {
  onSelectPlan?: (plan: string) => void;
}

export default function PricingSection({ onSelectPlan }: PricingSectionProps) {
  const titleRef = useFadeInUp();
  const cardsRef = useRef<HTMLDivElement>(null);

  const plans = [
    {
      name: "Basic",
      price: "$99",
      period: "year",
      description: "Perfect for individual crypto holders",
      features: [
        "1 wallet vault",
        "5 guardians",
        "3 beneficiaries",
        "90-day check-ins",
        "Email notifications",
        "256-bit encryption",
      ],
      isPopular: false,
    },
    {
      name: "Pro",
      price: "$299",
      period: "year",
      description: "Advanced controls for serious investors",
      features: [
        "5 wallet vaults",
        "Unlimited guardians",
        "Unlimited beneficiaries",
        "Custom check-in schedule",
        "SMS + Email notifications",
        "Progressive inheritance",
        "Priority support",
      ],
      isPopular: true,
    },
    {
      name: "Family",
      price: "$599",
      period: "year",
      description: "Complete solution for family wealth",
      features: [
        "Unlimited wallets",
        "Unlimited guardians",
        "Unlimited beneficiaries",
        "Lawyer integration",
        "Custom time-locks",
        "Dedicated support",
        "SOC2 compliance reports",
      ],
      isPopular: false,
    },
  ];

  useEffect(() => {
    if (!cardsRef.current) return;

    gsap.fromTo(
      cardsRef.current.children,
      {
        opacity: 0,
        y: 60,
        scale: 0.9,
      },
      {
        opacity: 1,
        y: 0,
        scale: 1,
        duration: 0.8,
        stagger: 0.15,
        ease: "back.out(1.2)",
        scrollTrigger: {
          trigger: cardsRef.current,
          start: "top 80%",
          toggleActions: "play none none reverse",
        },
      }
    );
  }, []);

  return (
    <section className="py-24 bg-card/30 relative overflow-hidden" id="pricing">
      <div className="absolute inset-0">
        <div className="absolute inset-0 bg-gradient-to-br from-primary/10 via-transparent to-accent/10" />
      </div>

      <div className="container mx-auto px-6 relative z-10">
        <div ref={titleRef} className="text-center mb-16">
          <h2 className="text-5xl font-bold font-display mb-4">
            Choose Your Protection Plan
          </h2>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
            Select the plan that best fits your crypto inheritance needs
          </p>
        </div>

        <div ref={cardsRef} className="grid md:grid-cols-3 gap-8 max-w-6xl mx-auto">
          {plans.map((plan) => (
            <PricingCard
              key={plan.name}
              {...plan}
              onSelect={() => onSelectPlan?.(plan.name)}
            />
          ))}
        </div>

        <div className="text-center mt-12">
          <p className="text-sm text-muted-foreground">
            Enterprise solutions available for institutions.{" "}
            <a href="#contact" className="text-primary hover:underline">
              Contact us
            </a>
          </p>
        </div>
      </div>
    </section>
  );
}

