import type { Express, Request, Response } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { ethers } from "ethers";
import bcrypt from "bcrypt";
import Stripe from "stripe";
import {
  insertUserSchema,
  insertVaultSchema,
  insertPartySchema,
  insertCheckInSchema,
  type Party,
  type ClaimAttestation,
} from "@shared/schema";
import { z } from "zod";
import {
  splitSecret,
  combineShares,
  encryptFragment,
  generateMockRecoveryPhrase,
  generateSecurePassphrase,
  deriveGuardianPassphrase,
} from "./services/shamir";
import "./types";
import { createOneTimeToken, verifyAndConsumeToken } from "./services/hmac";
import { sendGuardianNotificationLink } from "./services/notifications";
import crypto from "crypto";
import { getUploadUrl } from "./services/s3";

// Middleware to check if user is authenticated
function requireAuth(req: Request, res: Response, next: Function) {
  if (!req.session.userId) {
    return res.status(401).json({ message: "Unauthorized" });
  }
  next();
}

export async function registerRoutes(app: Express): Promise<Server> {
  // ===== Authentication Routes =====

  // Development mode: bypass MetaMask authentication
  app.post("/api/auth/dev-login", async (req, res) => {
    // Only allow in development
    if (process.env.NODE_ENV === "production") {
      return res.status(403).json({ message: "Not available in production" });
    }

    try {
      const devEmail = "dev@guardiavault.local";

      // Find or create dev user
      let user = await storage.getUserByEmail(devEmail);
      if (!user) {
        // Create dev user with email/password
        const hashedPassword = await bcrypt.hash("devpassword123", 10);
        user = await storage.createUser({
          email: devEmail,
          password: hashedPassword,
        });
      }

      // Set session
      req.session.userId = user.id;

      // Return user without password
      const { password: _, ...userWithoutPassword } = user;
      res.json({ user: userWithoutPassword });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Generate nonce for wallet signature
  app.post("/api/auth/nonce", async (req, res) => {
    try {
      const { walletAddress } = req.body;
      if (!walletAddress) {
        return res.status(400).json({ message: "Wallet address required" });
      }

      // Generate a random nonce for the user to sign
      const nonce = `GuardiaVault Authentication\n\nNonce: ${Math.random().toString(36).substring(2)}`;

      // Store nonce in session temporarily
      req.session.nonce = nonce;
      req.session.walletAddress = walletAddress.toLowerCase();

      res.json({ nonce });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Verify signature and authenticate user (legacy wallet-based auth)
  // NOTE: For backwards compatibility - new users should use email/password registration
  app.post("/api/auth/verify", async (req, res) => {
    try {
      const { signature } = req.body;

      if (!signature || !req.session.nonce || !req.session.walletAddress) {
        return res.status(400).json({ message: "Invalid request" });
      }

      // Verify the signature
      const recoveredAddress = ethers.verifyMessage(
        req.session.nonce,
        signature
      );

      if (recoveredAddress.toLowerCase() !== req.session.walletAddress) {
        return res.status(401).json({ message: "Invalid signature" });
      }

      // Find existing user by wallet
      let user = await storage.getUserByWallet(req.session.walletAddress);

      if (!user) {
        // For backwards compatibility, create user with wallet-based email
        // Users should migrate to email/password registration
        const walletEmail = `${req.session.walletAddress.toLowerCase()}@wallet.guardiavault.local`;
        const randomPassword = await bcrypt.hash(Math.random().toString(36), 10);

        user = await storage.createUser({
          email: walletEmail,
          password: randomPassword,
        });

        // Link wallet immediately
        user = await storage.linkWalletToUser(user.id, req.session.walletAddress) || user;
      }

      // Update last login
      req.session.userId = user.id;
      delete req.session.nonce;

      // Return user without password
      const { password: _, ...userWithoutPassword } = user;
      res.json({ user: userWithoutPassword });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Get current authenticated user
  app.get("/api/auth/me", requireAuth, async (req, res) => {
    try {
      const user = await storage.getUser(req.session.userId!);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      // Return user without password
      const { password: _, ...userWithoutPassword } = user;
      res.json({ user: userWithoutPassword });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Logout
  app.post("/api/auth/logout", (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ message: "Failed to logout" });
      }
      res.json({ message: "Logged out successfully" });
    });
  });

  // Email/Password Registration (with optional payment session)
  app.post("/api/auth/register", async (req, res) => {
    try {
      const { email, password, stripeSessionId, plan, months } = req.body;

      // Validate input
      const validated = insertUserSchema.parse({ email, password });

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(validated.email);
      if (existingUser) {
        return res.status(400).json({ message: "Email already registered" });
      }

      // TODO: In production, verify stripeSessionId with Stripe API
      // and store subscription info in database
      if (stripeSessionId) {
        console.log("User registered with payment:", {
          email: validated.email,
          sessionId: stripeSessionId,
          plan,
          months,
        });
        // Here you would:
        // 1. Verify the session with Stripe
        // 2. Store subscription details in database
        // 3. Grant access based on plan
      }

      // Hash password with bcrypt (10 rounds)
      const hashedPassword = await bcrypt.hash(validated.password, 10);

      // Create user
      const user = await storage.createUser({
        email: validated.email,
        password: hashedPassword,
      });

      // Set session
      req.session.userId = user.id;

      // Return user without password
      const { password: _, ...userWithoutPassword } = user;
      res.json({ user: userWithoutPassword });
    } catch (error: any) {
      if (error.name === "ZodError") {
        return res.status(400).json({ message: "Invalid input", errors: error.errors });
      }
      res.status(500).json({ message: error.message });
    }
  });

  // Email/Password Login
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = req.body;

      if (!email || !password) {
        return res.status(400).json({ message: "Email and password required" });
      }

      // Find user by email
      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(401).json({ message: "Invalid email or password" });
      }

      // Compare password
      const isValid = await bcrypt.compare(password, user.password);
      if (!isValid) {
        return res.status(401).json({ message: "Invalid email or password" });
      }

      // Set session
      req.session.userId = user.id;

      // Return user without password
      const { password: _, ...userWithoutPassword } = user;
      res.json({ user: userWithoutPassword });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Connect wallet to existing authenticated account
  app.post("/api/auth/connect-wallet", requireAuth, async (req, res) => {
    try {
      const { walletAddress, signature, message } = req.body;

      if (!walletAddress || !signature || !message) {
        return res.status(400).json({ message: "Wallet address, signature, and message required" });
      }

      // Verify the signature
      const recoveredAddress = ethers.verifyMessage(message, signature);
      if (recoveredAddress.toLowerCase() !== walletAddress.toLowerCase()) {
        return res.status(401).json({ message: "Invalid signature" });
      }

      // Check if wallet is already linked to another account
      const existingUser = await storage.getUserByWallet(walletAddress);
      if (existingUser && existingUser.id !== req.session.userId) {
        return res.status(400).json({ message: "Wallet already linked to another account" });
      }

      // Link wallet to current user
      const updatedUser = await storage.linkWalletToUser(req.session.userId!, walletAddress);
      if (!updatedUser) {
        return res.status(404).json({ message: "User not found" });
      }

      // Return user without password
      const { password: _, ...userWithoutPassword } = updatedUser;
      res.json({ user: userWithoutPassword });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // ===== Vault Routes =====

  // Get all vaults for current user
  app.get("/api/vaults", requireAuth, async (req, res) => {
    try {
      const vaults = await storage.getVaultsByOwner(req.session.userId!);
      res.json({ vaults });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Get specific vault
  app.get("/api/vaults/:id", requireAuth, async (req, res) => {
    try {
      const vault = await storage.getVault(req.params.id);

      if (!vault) {
        return res.status(404).json({ message: "Vault not found" });
      }

      if (vault.ownerId !== req.session.userId) {
        return res.status(403).json({ message: "Forbidden" });
      }

      res.json({ vault });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Create new vault with complete setup (guardians, beneficiaries, fragments)
  app.post("/api/vaults", requireAuth, async (req, res) => {
    let vault;
    let vaultMasterSecret;
    const createdGuardians = [];
    const createdBeneficiaries = [];
    const createdFragments = [];
    const guardianPassphrases = [];

    try {
      const {
        name,
        checkInIntervalDays,
        gracePeriodDays,
        guardians,
        beneficiaries,
        recoveryPhrase, // User can provide their own or we generate one
        masterSecret, // User's master secret for deriving guardian passphrases
      } = req.body;

      // Validate vault data
      const vaultData = insertVaultSchema.parse({
        name,
        checkInIntervalDays,
        gracePeriodDays,
        ownerId: req.session.userId,
      });

      // Create the vault
      vault = await storage.createVault(vaultData);

      // Add guardians if provided
      if (guardians && Array.isArray(guardians)) {
        for (const guardian of guardians) {
          const validated = insertPartySchema.parse({
            vaultId: vault.id,
            role: "guardian",
            name: guardian.name,
            email: guardian.email,
            phone: guardian.phone || null,
          });
          const created = await storage.createParty(validated);
          createdGuardians.push(created);
        }
      }

      // Add beneficiaries if provided
      if (beneficiaries && Array.isArray(beneficiaries)) {
        for (const beneficiary of beneficiaries) {
          const validated = insertPartySchema.parse({
            vaultId: vault.id,
            role: "beneficiary",
            name: beneficiary.name,
            email: beneficiary.email,
            phone: beneficiary.phone || null,
          });
          const created = await storage.createParty(validated);
          createdBeneficiaries.push(created);
        }
      }

      // Generate and distribute fragments to guardians
      if (createdGuardians.length >= 3) {
        // Use provided recovery phrase or generate mock one
        const secret = recoveryPhrase || generateMockRecoveryPhrase();

        // Use provided master secret or generate one
        // In production, this should be derived from user's wallet signature
        vaultMasterSecret = masterSecret || generateSecurePassphrase();

        // Split secret into shares (3-of-5 threshold, or 3-of-N for N guardians)
        const numShares = Math.min(5, createdGuardians.length);
        const { shares } = splitSecret(secret, 3, numShares);

        // Distribute fragments to guardians with secure encryption
        for (let i = 0; i < shares.length && i < createdGuardians.length; i++) {
          const guardian = createdGuardians[i];

          // Create derivation salt for this fragment (stored for regeneration)
          const derivationSalt = `${guardian.email}:fragment:${i}`;

          // Derive a passphrase for this guardian's fragment
          // This allows the user to regenerate passphrases if needed
          const passphrase = deriveGuardianPassphrase(
            vaultMasterSecret,
            guardian.email,
            i
          );

          // Encrypt the fragment using PBKDF2-derived key
          const encrypted = encryptFragment(shares[i], passphrase);

          const fragment = await storage.createFragment({
            vaultId: vault.id,
            guardianId: guardian.id,
            fragmentIndex: i,
            encryptedData: JSON.stringify(encrypted),
            derivationSalt: derivationSalt, // Store salt for deterministic regeneration
          });
          createdFragments.push(fragment);

          // Store passphrase info for response (not persisted on server)
          guardianPassphrases.push({
            guardianId: guardian.id,
            guardianName: guardian.name,
            guardianEmail: guardian.email,
            passphrase: passphrase,
          });
        }
      }

      // Create notification records for guardian invitations (processed by background job)
      if (createdGuardians.length > 0) {
        const owner = await storage.getUser(req.session.userId!);
        const ownerName = owner ? owner.email.split("@")[0] : "Vault Owner";

        const { notificationService } = await import("./services/notifications");

        for (const guardian of createdGuardians) {
          try {
            await notificationService.createGuardianInvitation({
              vaultId: vault.id,
              guardianId: guardian.id,
              guardianEmail: guardian.email,
              vaultName: vault.name,
              ownerName,
              inviteToken: guardian.inviteToken || "",
            });
            console.log(`ðŸ“§ Guardian invitation notification queued for ${guardian.email}`);
          } catch (notifError: any) {
            console.error(`Failed to create notification for guardian ${guardian.id}:`, notifError.message);
          }
        }
      }

      // Only return master secret if we generated it (not if user provided it)
      const shouldReturnMasterSecret = createdGuardians.length >= 3 && !masterSecret;

      res.json({
        vault,
        guardians: createdGuardians,
        beneficiaries: createdBeneficiaries,
        fragments: createdFragments,
        guardianPassphrases, // Return passphrases for user to distribute
        masterSecret: shouldReturnMasterSecret ? vaultMasterSecret : undefined,
        message: createdFragments.length > 0
          ? `Vault created successfully with ${createdFragments.length} encrypted fragments. Please securely distribute the passphrases to your guardians.`
          : "Vault created successfully. Add at least 3 guardians to generate fragments.",
      });
    } catch (error: any) {
      // Rollback: delete vault and all created entities if there was an error
      if (vault) {
        try {
          await storage.deleteVault(vault.id);
        } catch (rollbackError) {
          console.error("Failed to rollback vault creation:", rollbackError);
        }
      }

      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      res.status(500).json({ message: error.message });
    }
  });

  // Update vault
  app.patch("/api/vaults/:id", requireAuth, async (req, res) => {
    try {
      const vault = await storage.getVault(req.params.id);

      if (!vault) {
        return res.status(404).json({ message: "Vault not found" });
      }

      if (vault.ownerId !== req.session.userId) {
        return res.status(403).json({ message: "Forbidden" });
      }

      const updated = await storage.updateVault(req.params.id, req.body);
      res.json({ vault: updated });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Delete vault
  app.delete("/api/vaults/:id", requireAuth, async (req, res) => {
    try {
      const vault = await storage.getVault(req.params.id);

      if (!vault) {
        return res.status(404).json({ message: "Vault not found" });
      }

      if (vault.ownerId !== req.session.userId) {
        return res.status(403).json({ message: "Forbidden" });
      }

      await storage.deleteVault(req.params.id);
      res.json({ message: "Vault deleted successfully" });
