  // Upload death certificate file metadata (hash-only storage here)
  app.post("/api/claims/:claimId/upload", requireAuth, async (req, res) => {
    try {
      const { claimId } = req.params;
      const { fileName, mimeType, base64, useS3 } = req.body as any;
      if (!fileName || !mimeType) return res.status(400).json({ message: "fileName and mimeType required" });
      const claim = await storage.getDeathClaim(claimId);
      if (!claim) return res.status(404).json({ message: "Claim not found" });

      // S3 pre-sign flow
      if (useS3) {
        const bucket = process.env.S3_BUCKET || "";
        if (!bucket) return res.status(500).json({ message: "S3_BUCKET not set" });
        const key = `claims/${claim.id}/${Date.now()}-${fileName}`;
        const signed = await getUploadUrl({ bucket, key, contentType: mimeType });
        return res.json({ uploadUrl: signed.url, key });
      }

      // Direct upload fallback (base64)
      if (!base64) return res.status(400).json({ message: "base64 required when not using S3" });
      const buf = Buffer.from(base64, "base64");
      if (buf.length > 10 * 1024 * 1024) return res.status(400).json({ message: "File too large (10MB limit)" });
      const sha256 = crypto.createHash("sha256").update(buf).digest("hex");
      const rec = await storage.addClaimFile({ claimId, fileName, mimeType, size: buf.length, sha256 });
      if (claim.status === "open") await storage.updateDeathClaim(claim.id, { status: "under_review" } as any);
      return res.json({ file: rec });
    } catch (e: any) {
      return res.status(500).json({ message: e.message || "Failed to upload file" });
    }
  });

