      const claim = await storage.getDeathClaim(claimId);
      if (!claim) return res.status(404).json({ message: "Claim not found" });
      const rec = await storage.addClaimFile({ claimId, fileName, mimeType, size: Number(size), sha256 });
      if (claim.status === "open") await storage.updateDeathClaim(claim.id, { status: "under_review" } as any);
      return res.json({ file: rec });
