  // Attest (guardian/attestor approve/reject)
  app.post("/api/claims/:claimId/attest", requireAuth, async (req, res) => {
    try {
      const { claimId } = req.params;
      const { decision } = req.body as { decision: "approve" | "reject" };
      if (!decision) return res.status(400).json({ message: "decision required" });
      const claim = await storage.getDeathClaim(claimId);
      if (!claim) return res.status(404).json({ message: "Claim not found" });
      const vault = await storage.getVault(claim.vaultId);
      if (!vault) return res.status(404).json({ message: "Vault not found" });
      // User must be a guardian or attestor on this vault
      const parties = await storage.getPartiesByVault(vault.id);
      const currentUser = await storage.getUser(req.session.userId!);
      if (!currentUser) return res.status(401).json({ message: "User not found" });
      const myParty = parties.find((p: Party) => p.email.toLowerCase() === currentUser.email.toLowerCase());
      if (!myParty || (myParty.role !== "guardian" && myParty.role !== "attestor")) return res.status(403).json({ message: "Not authorized to attest" });

      const att = await storage.upsertClaimAttestation({ claimId, partyId: myParty.id, role: myParty.role, decision });

      // Threshold policy: require at least 2 guardian approvals and 1 attestor approval
      const all = await storage.listClaimAttestations(claimId);
      const gOk = all.filter((a: ClaimAttestation) => a.role === "guardian" && a.decision === "approve").length;
      const aOk = all.filter((a: ClaimAttestation) => a.role === "attestor" && a.decision === "approve").length;
      if (gOk >= 2 && aOk >= 1) {
        await storage.updateDeathClaim(claimId, { status: "approved" } as any);
      }
      if (all.some((a: ClaimAttestation) => a.decision === "reject")) {
        await storage.updateDeathClaim(claimId, { status: "rejected" } as any);
      }
      const updated = await storage.getDeathClaim(claimId);
      return res.json({ attestation: att, claim: updated });
    } catch (e: any) {
      return res.status(500).json({ message: e.message || "Failed to attest" });
    }
  });

