  app.post("/api/vaults/:vaultId/checkin", requireAuth, async (req, res) => {
    try {
      const vault = await storage.getVault(req.params.vaultId);

      if (!vault) {
        return res.status(404).json({ message: "Vault not found" });
      }

      if (vault.ownerId !== req.session.userId) {
        return res.status(403).json({ message: "Forbidden" });
      }

      // Get user to verify signature
      const user = await storage.getUser(req.session.userId!);
      if (!user) {
        return res.status(401).json({ message: "User not found" });
      }

      // Verify wallet signature for check-in
      const { message, signature } = req.body;
      if (!message || !signature) {
        return res.status(400).json({ message: "Message and signature required" });
      }

      // Check if user has a linked wallet
      if (!user.walletAddress) {
        return res.status(400).json({ message: "No wallet linked to account. Please link a wallet first." });
      }

      const recoveredAddress = ethers.verifyMessage(message, signature);
      if (recoveredAddress.toLowerCase() !== user.walletAddress.toLowerCase()) {
        return res.status(401).json({ message: "Invalid signature" });
      }

      // Validate check-in data
      const validated = insertCheckInSchema.parse({
        vaultId: req.params.vaultId,
        signature,
        ipAddress: req.ip || null,
      });

      // Record the check-in
      const checkIn = await storage.createCheckIn(validated);

      // Update vault's last check-in and next due date
      const now = new Date();
      const nextCheckInDue = new Date(
        now.getTime() + vault.checkInIntervalDays * 24 * 60 * 60 * 1000
      );

      const updatedVault = await storage.updateVault(req.params.vaultId, {
        lastCheckInAt: now,
        nextCheckInDue,
        status: "active",
      });

      res.json({ checkIn, vault: updatedVault });
    } catch (error: any) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
