  // Create Stripe Checkout Session (NO AUTH REQUIRED - payment first!)
  app.post("/api/payments/create-checkout-session", async (req, res) => {
    try {
      const { plan, months, email } = req.body;

      if (!STRIPE_KEY || !STRIPE_KEY.startsWith("sk_")) {
        return res.status(500).json({ message: "Stripe is not configured. Missing STRIPE_SECRET_KEY." });
      }

      // Annual pricing via Stripe price IDs
      const priceIds: Record<string, string | undefined> = {
        "Solo Plan": process.env.STRIPE_PRICE_SOLO,
        "Family Plan": process.env.STRIPE_PRICE_FAMILY,
        "Pro/Investor Plan": process.env.STRIPE_PRICE_PRO,
      };
      const priceId = priceIds[plan || ""];
      if (!priceId) {
        return res.status(500).json({ message: `Stripe price not configured for plan: ${plan}` });
      }

      // Ensure stripe is initialized
      if (!stripe) {
        return res.status(500).json({ message: "Stripe is not properly initialized" });
      }

      const origin = (req.headers.origin as string) || "http://localhost:5173";

      // Create Stripe checkout session
      const session = await stripe.checkout.sessions.create({
        payment_method_types: ["card"],
        line_items: [
          {
            price: priceId,
            quantity: 1,
          },
        ],
        mode: "payment",
        success_url: `${origin}/signup?session_id={CHECKOUT_SESSION_ID}&plan=${plan}&months=${months}`,
        cancel_url: `${origin}/checkout?plan=${plan}`,
        customer_email: email, // Collect email during checkout
        metadata: {
          plan,
          months: months.toString(),
          paymentCompleted: "true",
        },
      });

      let url = session.url;
      if (!url && session.id) {
        // Fallback: construct hosted checkout URL when API omits url
        url = `https://checkout.stripe.com/c/pay/${session.id}`;
        console.warn("Checkout session missing url; using constructed URL", { id: session.id, url });
      }
      if (!url) {
        console.error("Stripe created session without URL and no id");
        return res.status(500).json({ message: "Stripe did not return a checkout URL" });
      }

      res.json({ url, id: session.id });
    } catch (error: any) {
      console.error("Stripe checkout error:", error);
      res.status(500).json({ message: error.message || "Failed to create checkout session" });
    }
  });

