      res.status(400).json({ message: "Webhook handler failed" });
    }
  });

  // ===== Notification Routes =====
  const { notificationService } = await import("./services/notifications");

  // Send guardian invitation
  app.post("/api/notifications/send-guardian-invite", requireAuth, async (req, res) => {
    try {
      const { vaultId, guardianId } = req.body;

      if (!vaultId || !guardianId) {
        return res.status(400).json({ message: "vaultId and guardianId required" });
      }

      const vault = await storage.getVault(vaultId);
      if (!vault) {
        return res.status(404).json({ message: "Vault not found" });
      }

      if (vault.ownerId !== req.session.userId) {
        return res.status(403).json({ message: "Forbidden" });
      }

      const guardian = await storage.getParty(guardianId);
      if (!guardian || guardian.role !== "guardian") {
        return res.status(404).json({ message: "Guardian not found" });
      }

      const owner = await storage.getUser(vault.ownerId);
      if (!owner) {
        return res.status(404).json({ message: "Owner not found" });
      }

      const notification = await notificationService.createGuardianInvitation({
        vaultId: vault.id,
        guardianId: guardian.id,
        guardianEmail: guardian.email,
        vaultName: vault.name,
        ownerName: owner.email.split("@")[0],
        inviteToken: guardian.inviteToken || "",
      });

      res.json({ notification, message: "Guardian invitation queued" });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Send beneficiary alert
  app.post("/api/notifications/send-beneficiary-alert", requireAuth, async (req, res) => {
    try {
      const { vaultId, beneficiaryId } = req.body;

      if (!vaultId || !beneficiaryId) {
        return res.status(400).json({ message: "vaultId and beneficiaryId required" });
      }

      const vault = await storage.getVault(vaultId);
      if (!vault) {
        return res.status(404).json({ message: "Vault not found" });
      }

      if (vault.ownerId !== req.session.userId) {
        return res.status(403).json({ message: "Forbidden" });
      }

      const beneficiary = await storage.getParty(beneficiaryId);
      if (!beneficiary || beneficiary.role !== "beneficiary") {
        return res.status(404).json({ message: "Beneficiary not found" });
      }

      const notification = await notificationService.createBeneficiaryNotification({
        vaultId: vault.id,
        beneficiaryEmail: beneficiary.email,
        vaultName: vault.name,
      });

      res.json({ notification, message: "Beneficiary notification queued" });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Send check-in reminder
  app.post("/api/notifications/send-checkin-reminder", requireAuth, async (req, res) => {
    try {
      const { vaultId } = req.body;

      if (!vaultId) {
        return res.status(400).json({ message: "vaultId required" });
      }

      const vault = await storage.getVault(vaultId);
      if (!vault) {
        return res.status(404).json({ message: "Vault not found" });
      }

      if (vault.ownerId !== req.session.userId) {
        return res.status(403).json({ message: "Forbidden" });
      }

      const owner = await storage.getUser(vault.ownerId);
      if (!owner) {
        return res.status(404).json({ message: "Owner not found" });
      }

      const now = new Date();
      const dueDate = new Date(vault.nextCheckInDue);
      const daysRemaining = Math.ceil((dueDate.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));

      const notification = await notificationService.createCheckInReminder({
        vaultId: vault.id,
        ownerEmail: owner.email,
        ownerName: owner.email.split("@")[0],
        vaultName: vault.name,
        daysRemaining,
        dueDate: dueDate.toLocaleDateString(),
        gracePeriod: vault.gracePeriodDays,
      });

      res.json({ notification, message: "Check-in reminder queued" });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Get pending notifications (admin/debugging)
  app.get("/api/notifications/pending", requireAuth, async (req, res) => {
    try {
      const pending = await storage.getPendingNotifications();
      res.json({ notifications: pending, count: pending.length });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Manually trigger batch processing (testing)
  app.post("/api/notifications/process-pending", requireAuth, async (req, res) => {
    try {
      const stats = await notificationService.processPendingNotifications();
      res.json({ 
        message: "Processing complete",
        stats 
      });
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Test notification sending (demo mode)
  app.post("/api/notifications/test-send", requireAuth, async (req, res) => {
    try {
      const { channel, recipient } = req.body;

      if (!channel || !recipient) {
        return res.status(400).json({ message: "channel and recipient required" });
      }

      if (channel === "email") {
        const testData = {
          vaultName: "Test Vault",
          ownerName: "Test Owner",
          acceptLink: "http://localhost:5000/accept-invite?token=test123",
        };

        const { html, text, subject } = await notificationService.loadTemplate(
          "guardian-invitation",
          testData
        );

        const result = await notificationService.sendEmail(
          recipient,
          subject,
          html,
          text
        );

        res.json({ result, message: "Test email sent" });
      } else if (channel === "sms") {
        const result = await notificationService.sendSMS(
          recipient,
          "GuardiaVault Test: This is a test SMS from your notification system."
        );

        res.json({ result, message: "Test SMS sent" });
      } else {
        res.status(400).json({ message: "Unsupported channel" });
      }
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

