import { useWallet } from "@/hooks/useWallet";
import { Button } from "@/components/ui/button";
import { Wallet } from "lucide-react";
import { Loader2 } from "lucide-react";

interface WalletConnectButtonProps {
  variant?: "default" | "outline" | "ghost";
  size?: "default" | "sm" | "lg";
  className?: string;
}

export function WalletConnectButton({
  variant = "default",
  size = "default",
  className = "",
}: WalletConnectButtonProps) {
  const { isConnected, isConnecting, walletAddress, connectWallet, disconnectWallet } = useWallet();

  if (isConnected && walletAddress) {
    return (
      <Button
        variant={variant}
        size={size}
        onClick={disconnectWallet}
        className={className}
        data-testid="button-disconnect-wallet"
      >
        <Wallet className="w-4 h-4" />
        {walletAddress.slice(0, 6)}...{walletAddress.slice(-4)}
      </Button>
    );
  }

  return (
    <Button
      variant={variant}
      size={size}
      onClick={connectWallet}
      disabled={isConnecting}
      className={className}
      data-testid="button-connect-wallet"
    >
      {isConnecting ? (
        <>
          <Loader2 className="w-4 h-4 animate-spin" />
          Connecting...
        </>
      ) : (
        <>
          <Wallet className="w-4 h-4" />
          Connect Wallet
        </>
      )}
    </Button>
  );
}

client/src/hooks/useWallet.tsx.content:
import { createContext, useContext, useState, useEffect, ReactNode } from "react";
import { BrowserProvider } from "ethers";
import { useToast } from "@/hooks/use-toast";
import type { User } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";

interface WalletContextType {
  isConnected: boolean;
  isConnecting: boolean;
  walletAddress: string | null;
  user: User | null;
  connectWallet: () => Promise<void>;
  disconnectWallet: () => Promise<void>;
}

const WalletContext = createContext<WalletContextType | undefined>(undefined);

export function WalletProvider({ children }: { children: ReactNode }) {
  const [isConnected, setIsConnected] = useState(false);
  const [isConnecting, setIsConnecting] = useState(false);
  const [walletAddress, setWalletAddress] = useState<string | null>(null);
  const [user, setUser] = useState<User | null>(null);
  const { toast } = useToast();

  // Check if user is already authenticated
  useEffect(() => {
    checkAuth();
  }, []);

  async function checkAuth() {
    try {
      const response = await fetch("/api/auth/me");
      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        setWalletAddress(data.user.walletAddress);
        setIsConnected(true);
      }
    } catch (error) {
      // Not authenticated, do nothing
    }
  }

  async function connectWallet() {
    if (!window.ethereum) {
      toast({
        title: "Wallet Not Found",
        description: "Please install MetaMask or another Web3 wallet to continue.",
        variant: "destructive",
      });
      return;
    }

    setIsConnecting(true);

    try {
      // Request account access
      const provider = new BrowserProvider(window.ethereum);
      const accounts = await provider.send("eth_requestAccounts", []);
      const address = accounts[0];

      // Get nonce from backend
      const nonceResponse = await apiRequest(
        "POST",
        "/api/auth/nonce",
        { walletAddress: address }
      );

      if (!nonceResponse.ok) {
        throw new Error("Failed to get authentication nonce");
      }

      const { nonce } = await nonceResponse.json();

      // Sign the nonce with the wallet
      const signer = await provider.getSigner();
      const signature = await signer.signMessage(nonce);

      // Verify signature with backend
      const verifyResponse = await apiRequest(
        "POST",
        "/api/auth/verify",
        { signature }
      );

      if (!verifyResponse.ok) {
        throw new Error("Failed to verify signature");
      }

      const { user: authenticatedUser } = await verifyResponse.json();

      setUser(authenticatedUser);
      setWalletAddress(authenticatedUser.walletAddress);
      setIsConnected(true);

      // Invalidate queries to refetch data
      queryClient.invalidateQueries();

      toast({
        title: "Wallet Connected",
        description: `Successfully connected to ${authenticatedUser.walletAddress.slice(0, 6)}...${authenticatedUser.walletAddress.slice(-4)}`,
      });
    } catch (error: any) {
      toast({
        title: "Connection Failed",
        description: error.message || "Failed to connect wallet",
        variant: "destructive",
      });
    } finally {
      setIsConnecting(false);
    }
  }

  async function disconnectWallet() {
    try {
      await apiRequest("POST", "/api/auth/logout");

      setUser(null);
      setWalletAddress(null);
      setIsConnected(false);

      // Invalidate all queries to refetch with unauthenticated state
      queryClient.invalidateQueries();

      toast({
        title: "Wallet Disconnected",
        description: "You have been logged out successfully.",
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: "Failed to disconnect wallet",
        variant: "destructive",
      });
    }
  }

  return (
    <WalletContext.Provider
      value={{
        isConnected,
        isConnecting,
        walletAddress,
        user,
        connectWallet,
        disconnectWallet,
      }}
    >
      {children}
    </WalletContext.Provider>
  );
}

export function useWallet() {
  const context = useContext(WalletContext);
  if (!context) {
    throw new Error("useWallet must be used within WalletProvider");
  }
  return context;
}

// Type declaration for window.ethereum
declare global {
  interface Window {
    ethereum?: any;
  }
}

client/src/hooks/useSubscriptionEscrow.t.content:
import { useWriteContract, useReadContract, useWaitForTransactionReceipt, useAccount } from "wagmi";
import { parseEther, formatEther, type Address } from "viem";
import { useToast } from "@/hooks/use-toast";
import { useEffect } from "react";

const ESCROW_CONTRACT_ADDRESS = (import.meta.env.VITE_ESCROW_CONTRACT_ADDRESS || "0x0000000000000000000000000000000000000000") as Address;

const SUBSCRIPTION_ESCROW_ABI = [
  {
    "inputs": [
      { "internalType": "uint256", "name": "monthlyRate", "type": "uint256" },
      { "internalType": "uint256", "name": "prepaidMonths", "type": "uint256" }
    ],
    "name": "createSubscription",
    "outputs": [],
    "stateMutability": "payable",
    "type": "function"
  },
  {
    "inputs": [
      { "internalType": "address", "name": "subscriber", "type": "address" }
    ],
    "name": "getSubscriptionStatus",
    "outputs": [
      {
        "components": [
          { "internalType": "address", "name": "subscriber", "type": "address" },
          { "internalType": "uint256", "name": "monthlyRate", "type": "uint256" },
          { "internalType": "uint256", "name": "prepaidMonths", "type": "uint256" },
          { "internalType": "uint256", "name": "monthsConsumed", "type": "uint256" },
          { "internalType": "uint256", "name": "lastPaymentTime", "type": "uint256" },
          { "internalType": "uint256", "name": "depositAmount", "type": "uint256" },
          { "internalType": "bool", "name": "active", "type": "bool" },
          { "internalType": "bool", "name": "vaultTriggered", "type": "bool" },
          { "internalType": "uint256", "name": "triggerTime", "type": "uint256" }
        ],
        "internalType": "struct SubscriptionEscrow.Subscription",
        "name": "",
        "type": "tuple"
      }
    ],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [
      { "internalType": "address", "name": "subscriber", "type": "address" }
    ],
    "name": "getRemainingMonths",
    "outputs": [
      { "internalType": "uint256", "name": "", "type": "uint256" }
    ],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [
      { "internalType": "address", "name": "subscriber", "type": "address" }
    ],
    "name": "isSubscriptionActive",
    "outputs": [
      { "internalType": "bool", "name": "", "type": "bool" }
    ],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [],
    "name": "MIN_PREPAID_MONTHS",
    "outputs": [
      { "internalType": "uint256", "name": "", "type": "uint256" }
    ],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [],
    "name": "MAX_PREPAID_MONTHS",
    "outputs": [
      { "internalType": "uint256", "name": "", "type": "uint256" }
    ],
    "stateMutability": "view",
    "type": "function"
  }
] as const;

export interface Subscription {
  subscriber: Address;
  monthlyRate: bigint;
  prepaidMonths: bigint;
  monthsConsumed: bigint;
  lastPaymentTime: bigint;
  depositAmount: bigint;
  active: boolean;
  vaultTriggered: boolean;
  triggerTime: bigint;
}

export function useCreateSubscription() {
  const { toast } = useToast();
  const { writeContract, data: hash, isPending, error } = useWriteContract();

  const { isLoading: isConfirming, isSuccess } = useWaitForTransactionReceipt({
    hash,
  });

  useEffect(() => {
    if (error) {
      toast({
        title: "Transaction Failed",
        description: error.message || "Failed to create subscription",
        variant: "destructive",
      });
    }
  }, [error, toast]);

  useEffect(() => {
    if (isSuccess) {
      toast({
        title: "Subscription Created!",
        description: "Your subscription has been successfully created on-chain",
      });
    }
  }, [isSuccess, toast]);

  const createSubscription = (monthlyRateEth: string, prepaidMonths: number) => {
    const monthlyRateWei = parseEther(monthlyRateEth);
    const totalValue = monthlyRateWei * BigInt(prepaidMonths);

    writeContract({
      address: ESCROW_CONTRACT_ADDRESS,
      abi: SUBSCRIPTION_ESCROW_ABI,
      functionName: "createSubscription",
      args: [monthlyRateWei, BigInt(prepaidMonths)],
      value: totalValue,
    });
  };

  return {
    createSubscription,
    hash,
    isPending,
    isConfirming,
    isSuccess,
    error,
  };
}

export function useSubscriptionStatus(subscriberAddress?: Address) {
  const { address } = useAccount();
  const targetAddress = subscriberAddress || address;

  const { data, isLoading, isError, refetch } = useReadContract({
    address: ESCROW_CONTRACT_ADDRESS,
    abi: SUBSCRIPTION_ESCROW_ABI,
    functionName: "getSubscriptionStatus",
    args: targetAddress ? [targetAddress] : undefined,
    query: {
      enabled: !!targetAddress,
    },
  });

  const subscription = data ? {
    subscriber: (data as any)[0],
    monthlyRate: (data as any)[1],
    prepaidMonths: (data as any)[2],
    monthsConsumed: (data as any)[3],
    lastPaymentTime: (data as any)[4],
    depositAmount: (data as any)[5],
    active: (data as any)[6],
    vaultTriggered: (data as any)[7],
    triggerTime: (data as any)[8],
  } as Subscription : null;

  return {
    subscription,
    isLoading,
    isError,
    refetch,
  };
}

export function useRemainingMonths(subscriberAddress?: Address) {
  const { address } = useAccount();
  const targetAddress = subscriberAddress || address;

  const { data, isLoading, isError, refetch } = useReadContract({
    address: ESCROW_CONTRACT_ADDRESS,
    abi: SUBSCRIPTION_ESCROW_ABI,
    functionName: "getRemainingMonths",
    args: targetAddress ? [targetAddress] : undefined,
    query: {
      enabled: !!targetAddress,
    },
  });

  return {
    remainingMonths: data !== undefined ? Number(data) : null,
    isLoading,
    isError,
    refetch,
  };
}

export function useIsSubscriptionActive(subscriberAddress?: Address) {
  const { address } = useAccount();
  const targetAddress = subscriberAddress || address;

  const { data, isLoading, isError, refetch } = useReadContract({
    address: ESCROW_CONTRACT_ADDRESS,
    abi: SUBSCRIPTION_ESCROW_ABI,
    functionName: "isSubscriptionActive",
    args: targetAddress ? [targetAddress] : undefined,
    query: {
      enabled: !!targetAddress,
    },
  });

  return {
    isActive: data || false,
    isLoading,
    isError,
    refetch,
  };
}

export function useSubscriptionLimits() {
  const { data: minMonths } = useReadContract({
    address: ESCROW_CONTRACT_ADDRESS,
    abi: SUBSCRIPTION_ESCROW_ABI,
    functionName: "MIN_PREPAID_MONTHS",
  });

  const { data: maxMonths } = useReadContract({
    address: ESCROW_CONTRACT_ADDRESS,
    abi: SUBSCRIPTION_ESCROW_ABI,
    functionName: "MAX_PREPAID_MONTHS",
  });

  return {
    minPrepaidMonths: minMonths !== undefined ? Number(minMonths) : 1,
    maxPrepaidMonths: maxMonths !== undefined ? Number(maxMonths) : 24,
  };
}

export { ESCROW_CONTRACT_ADDRESS };

client/src/pages/CreateVault.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useCreateVault } from "@/hooks/useVaults";
import PassphraseDisplay from "@/components/PassphraseDisplay";
import { Button } from "@/components/ui/button";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { ArrowLeft, ArrowRight, Check, Plus, Trash2, Shield, Users, Eye, Wallet } from "lucide-react";
import { useWallet } from "@/hooks/useWallet";
import { useCreateSubscription, useSubscriptionLimits, useIsSubscriptionActive } from "@/hooks/useSubscriptionEscrow";
import { Alert, AlertDescription, AlertTitle } from "@/components/ui/alert";
import { formatEther } from "viem";
import { Slider } from "@/components/ui/slider";
import { useToast } from "@/hooks/use-toast";

const partySchema = z.object({
  name: z.string().min(2, "Name must be at least 2 characters"),
  email: z.string().email("Invalid email address"),
  phone: z.string().optional(),
});

const vaultFormSchema = z.object({
  name: z.string().min(3, "Vault name must be at least 3 characters"),
  checkInIntervalDays: z.number().min(1).max(365).default(90),
  gracePeriodDays: z.number().min(1).max(90).default(7),
  guardians: z.array(partySchema).min(3, "Minimum 3 guardians required").max(5, "Maximum 5 guardians allowed"),
  beneficiaries: z.array(partySchema).min(1, "At least 1 beneficiary required"),
});

type VaultFormValues = z.infer<typeof vaultFormSchema>;

const STEPS = [
  { id: 1, title: "Subscription", icon: Wallet },
  { id: 2, title: "Vault Setup", icon: Shield },
  { id: 3, title: "Guardians", icon: Users },
  { id: 4, title: "Beneficiaries", icon: Eye },
  { id: 5, title: "Review", icon: Check },
];

interface VaultCreationResponse {
  vault: any;
  guardians: any[];
  beneficiaries: any[];
  fragments: any[];
  guardianPassphrases?: Array<{
    guardianId: string;
    guardianName: string;
    guardianEmail: string;
    passphrase: string;
  }>;
  masterSecret?: string;
  message: string;
}

export default function CreateVault() {
  const [, setLocation] = useLocation();
  const { walletAddress } = useWallet();
  const { toast } = useToast();
  const [currentStep, setCurrentStep] = useState(1);
  const [creationResponse, setCreationResponse] = useState<VaultCreationResponse | null>(null);
  const createVault = useCreateVault();

  const [prepaidMonths, setPrepaidMonths] = useState(12);
  const monthlyRateEth = "0.01";

  const { createSubscription, isPending, isConfirming, isSuccess, hash } = useCreateSubscription();
  const { minPrepaidMonths, maxPrepaidMonths } = useSubscriptionLimits();
  const { isActive: hasActiveSubscription, refetch: refetchSubscription } = useIsSubscriptionActive();

  // Refetch subscription status after transaction confirms
  useEffect(() => {
    if (isSuccess) {
      // Wait briefly for blockchain state to settle, then refetch
      const timer = setTimeout(() => {
        refetchSubscription();
      }, 1000);

      return () => clearTimeout(timer);
    }
  }, [isSuccess, refetchSubscription]);

  const form = useForm<VaultFormValues>({
    resolver: zodResolver(vaultFormSchema),
    defaultValues: {
      name: "",
      checkInIntervalDays: 90,
      gracePeriodDays: 7,
      guardians: [],
      beneficiaries: [],
    },
  });

  const handleNext = async () => {
    let isValid = false;

    if (currentStep === 1) {
      if (!hasActiveSubscription) {
        toast({
          title: "Subscription Required",
          description: "Please create a subscription first to proceed to vault creation",
          variant: "destructive",
        });
        return;
      }
      isValid = true;
    } else if (currentStep === 2) {
      isValid = await form.trigger(["name", "checkInIntervalDays", "gracePeriodDays"]);
    } else if (currentStep === 3) {
      isValid = await form.trigger("guardians");
    } else if (currentStep === 4) {
      isValid = await form.trigger("beneficiaries");
    }

    if (isValid) {
      setCurrentStep((prev) => Math.min(prev + 1, STEPS.length));
    }
  };

  const handleCreateSubscription = () => {
    createSubscription(monthlyRateEth, prepaidMonths);
  };

  const handleBack = () => {
    setCurrentStep((prev) => Math.max(prev - 1, 1));
  };

  const onSubmit = async (data: VaultFormValues) => {
    const response = await createVault.mutateAsync(data);
    setCreationResponse(response as VaultCreationResponse);
  };

  const handleClosePassphrases = () => {
    setLocation("/dashboard");
  };

  // Show passphrase display after successful creation
  if (creationResponse && creationResponse.guardianPassphrases && creationResponse.guardianPassphrases.length > 0) {
    return (
      <div className="min-h-screen bg-background">
        <div className="container max-w-4xl mx-auto py-8 px-4">
          <div className="mb-8">
            <h1 className="text-3xl font-bold mb-2">Vault Created Successfully!</h1>
            <p className="text-muted-foreground">
              Your vault "{creationResponse.vault.name}" has been created with encrypted fragments. Please save and
              distribute the passphrases below.
            </p>
          </div>

          <PassphraseDisplay
            guardianPassphrases={creationResponse.guardianPassphrases}
            masterSecret={creationResponse.masterSecret}
            vaultName={creationResponse.vault.name}
            onClose={handleClosePassphrases}
          />
        </div>
      </div>
    );
  }

  const addGuardian = () => {
    const current = form.getValues("guardians");
    if (current.length < 5) {
      form.setValue("guardians", [...current, { name: "", email: "", phone: "" }]);
    }
  };

  const removeGuardian = (index: number) => {
    const current = form.getValues("guardians");
    form.setValue(
      "guardians",
      current.filter((_, i) => i !== index)
    );
  };

  const addBeneficiary = () => {
    const current = form.getValues("beneficiaries");
    form.setValue("beneficiaries", [...current, { name: "", email: "", phone: "" }]);
  };

  const removeBeneficiary = (index: number) => {
    const current = form.getValues("beneficiaries");
    form.setValue(
      "beneficiaries",
      current.filter((_, i) => i !== index)
    );
  };

  if (!walletAddress) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center p-4">
        <Card className="max-w-md">
          <CardHeader>
            <CardTitle>Wallet Required</CardTitle>
            <CardDescription>Please connect your wallet to create a vault</CardDescription>
          </CardHeader>
          <CardContent>
            <Button onClick={() => setLocation("/")} className="w-full" data-testid="button-back-to-home">
              Go to Home
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  const progress = (currentStep / STEPS.length) * 100;

  return (
    <div className="min-h-screen bg-background">
      <div className="container max-w-4xl mx-auto py-8 px-4">
        <div className="mb-8">
          <Button variant="ghost" onClick={() => setLocation("/dashboard")} className="mb-4" data-testid="button-back-to-dashboard">
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Dashboard
          </Button>
          <h1 className="text-3xl font-bold mb-2">Create New Vault</h1>
          <p className="text-muted-foreground">Set up your cryptographic dead man's switch</p>
        </div>

        <div className="mb-8">
          <Progress value={progress} className="mb-4" />
          <div className="flex justify-between">
            {STEPS.map((step) => {
              const Icon = step.icon;
              const isActive = currentStep === step.id;
              const isComplete = currentStep > step.id;

              return (
                <div key={step.id} className="flex flex-col items-center flex-1">
                  <div
                    className={`w-10 h-10 rounded-full flex items-center justify-center mb-2 transition-colors ${
                      isActive
                        ? "bg-primary text-primary-foreground"
                        : isComplete
                        ? "bg-primary/20 text-primary"
                        : "bg-muted text-muted-foreground"
                    }`}
                  >
                    {isComplete ? <Check className="w-5 h-5" /> : <Icon className="w-5 h-5" />}
                  </div>
                  <span className={`text-xs text-center ${isActive ? "font-semibold" : "text-muted-foreground"}`}>
                    {step.title}
                  </span>
                </div>
              );
            })}
          </div>
        </div>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            {currentStep === 1 && (
              <Card>
                <CardHeader>
                  <CardTitle>Create Subscription</CardTitle>
                  <CardDescription>
                    Before creating your vault, you need an active subscription on-chain. This prepaid subscription ensures your vault remains operational.
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-6">
                  {hasActiveSubscription ? (
                    <Alert>
                      <Check className="h-4 w-4" />
                      <AlertTitle>Active Subscription Found</AlertTitle>
                      <AlertDescription>
                        You already have an active subscription. You can proceed to vault creation.
                      </AlertDescription>
                    </Alert>
                  ) : (
                    <>
                      <div className="space-y-4">
                        <div>
                          <label className="text-sm font-medium mb-2 block">
                            Prepaid Months: {prepaidMonths} months
                          </label>
                          <Slider
                            value={[prepaidMonths]}
                            onValueChange={(value) => setPrepaidMonths(value[0])}
                            min={minPrepaidMonths}
                            max={maxPrepaidMonths}
                            step={1}
                            className="w-full"
                            data-testid="slider-prepaid-months"
                          />
                          <p className="text-xs text-muted-foreground mt-2">
                            Range: {minPrepaidMonths} - {maxPrepaidMonths} months
                          </p>
                        </div>

                        <div className="bg-muted p-4 rounded-lg space-y-2">
                          <div className="flex justify-between">
                            <span className="text-sm">Monthly Rate:</span>
                            <span className="font-mono font-semibold">{monthlyRateEth} ETH</span>
                          </div>
                          <div className="flex justify-between">
                            <span className="text-sm">Prepaid Months:</span>
                            <span className="font-semibold">{prepaidMonths}</span>
                          </div>
                          <div className="border-t border-border pt-2 mt-2">
                            <div className="flex justify-between">
                              <span className="font-semibold">Total Payment:</span>
                              <span className="font-mono font-bold text-primary">
                                {(parseFloat(monthlyRateEth) * prepaidMonths).toFixed(4)} ETH
                              </span>
                            </div>
                          </div>
                        </div>

                        <Alert>
                          <AlertDescription className="text-xs">
                            <strong>Important:</strong> This subscription payment is sent to the escrow contract on-chain.
                            Payments release monthly to the platform. If your vault triggers (death detected), the subscription
                            auto-extends by 6 months to ensure continuous guardian access.
                          </AlertDescription>
                        </Alert>

                        {hash && (
                          <Alert>
                            <AlertTitle>Transaction Submitted</AlertTitle>
                            <AlertDescription className="text-xs font-mono break-all">
                              Hash: {hash}
                              {isConfirming && <p className="mt-2">Waiting for confirmation...</p>}
                              {isSuccess && <p className="mt-2 text-green-600">âœ“ Transaction confirmed!</p>}
                            </AlertDescription>
                          </Alert>
                        )}

                        <Button
                          type="button"
                          onClick={handleCreateSubscription}
                          disabled={isPending || isConfirming}
                          className="w-full"
                          data-testid="button-create-subscription"
                        >
                          {isPending || isConfirming ? (
                            <>Processing Transaction...</>
                          ) : (
                            <>
                              <Wallet className="w-4 h-4 mr-2" />
                              Create Subscription ({(parseFloat(monthlyRateEth) * prepaidMonths).toFixed(4)} ETH)
                            </>
                          )}
                        </Button>
                      </div>
                    </>
                  )}
                </CardContent>
              </Card>
            )}

            {currentStep === 2 && (
              <Card>
                <CardHeader>
                  <CardTitle>Vault Configuration</CardTitle>
                  <CardDescription>Define the basic settings for your vault</CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <FormField
                    control={form.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Vault Name</FormLabel>
                        <FormControl>
                          <Input placeholder="My Crypto Inheritance Vault" {...field} data-testid="input-vault-name" />
                        </FormControl>
                        <FormDescription>A memorable name for this vault</FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="checkInIntervalDays"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Check-In Interval (days)</FormLabel>
                        <FormControl>
                          <Input
                            type="number"
                            {...field}
                            onChange={(e) => field.onChange(parseInt(e.target.value))}
                            data-testid="input-checkin-interval"
                          />
                        </FormControl>
                        <FormDescription>How often you need to check in (default: 90 days)</FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="gracePeriodDays"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Grace Period (days)</FormLabel>
                        <FormControl>
                          <Input
                            type="number"
                            {...field}
                            onChange={(e) => field.onChange(parseInt(e.target.value))}
                            data-testid="input-grace-period"
                          />
                        </FormControl>
                        <FormDescription>Additional time before vault triggers (default: 7 days)</FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </CardContent>
              </Card>
            )}

            {currentStep === 3 && (
              <Card>
                <CardHeader>
                  <CardTitle>Add Guardians</CardTitle>
                  <CardDescription>
                    Guardians hold encrypted key fragments. Minimum 3 required (3-of-5 threshold).
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  {form.watch("guardians").map((_, index) => (
                    <Card key={index}>
                      <CardContent className="pt-6 space-y-4">
                        <div className="flex items-center justify-between mb-2">
                          <h4 className="font-semibold">Guardian {index + 1}</h4>
                          <Button
                            type="button"
                            variant="ghost"
                            size="icon"
                            onClick={() => removeGuardian(index)}
                            data-testid={`button-remove-guardian-${index}`}
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>

                        <FormField
                          control={form.control}
                          name={`guardians.${index}.name`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Name</FormLabel>
                              <FormControl>
                                <Input placeholder="John Doe" {...field} data-testid={`input-guardian-name-${index}`} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name={`guardians.${index}.email`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Email</FormLabel>
                              <FormControl>
                                <Input
                                  type="email"
                                  placeholder="john@example.com"
                                  {...field}
                                  data-testid={`input-guardian-email-${index}`}
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name={`guardians.${index}.phone`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Phone (optional)</FormLabel>
                              <FormControl>
                                <Input
                                  placeholder="+1234567890"
                                  {...field}
                                  data-testid={`input-guardian-phone-${index}`}
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </CardContent>
                    </Card>
                  ))}

                  {form.watch("guardians").length < 5 && (
                    <Button type="button" variant="outline" onClick={addGuardian} className="w-full" data-testid="button-add-guardian">
                      <Plus className="w-4 h-4 mr-2" />
                      Add Guardian ({form.watch("guardians").length}/5)
                    </Button>
                  )}

                  {form.formState.errors.guardians && (
                    <p className="text-sm text-destructive">{form.formState.errors.guardians.message}</p>
                  )}
                </CardContent>
              </Card>
            )}

            {currentStep === 4 && (
              <Card>
                <CardHeader>
                  <CardTitle>Add Beneficiaries</CardTitle>
                  <CardDescription>Beneficiaries will receive access when the vault triggers</CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  {form.watch("beneficiaries").map((_, index) => (
                    <Card key={index}>
                      <CardContent className="pt-6 space-y-4">
                        <div className="flex items-center justify-between mb-2">
                          <h4 className="font-semibold">Beneficiary {index + 1}</h4>
                          <Button
                            type="button"
                            variant="ghost"
                            size="icon"
                            onClick={() => removeBeneficiary(index)}
                            data-testid={`button-remove-beneficiary-${index}`}
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>

                        <FormField
                          control={form.control}
                          name={`beneficiaries.${index}.name`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Name</FormLabel>
                              <FormControl>
                                <Input placeholder="Jane Doe" {...field} data-testid={`input-beneficiary-name-${index}`} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name={`beneficiaries.${index}.email`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Email</FormLabel>
                              <FormControl>
                                <Input
                                  type="email"
                                  placeholder="jane@example.com"
                                  {...field}
                                  data-testid={`input-beneficiary-email-${index}`}
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name={`beneficiaries.${index}.phone`}
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Phone (optional)</FormLabel>
                              <FormControl>
                                <Input
                                  placeholder="+1234567890"
                                  {...field}
                                  data-testid={`input-beneficiary-phone-${index}`}
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </CardContent>
                    </Card>
                  ))}

                  <Button type="button" variant="outline" onClick={addBeneficiary} className="w-full" data-testid="button-add-beneficiary">
                    <Plus className="w-4 h-4 mr-2" />
                    Add Beneficiary
                  </Button>

                  {form.formState.errors.beneficiaries && (
                    <p className="text-sm text-destructive">{form.formState.errors.beneficiaries.message}</p>
                  )}
                </CardContent>
              </Card>
            )}

            {currentStep === 5 && (
              <Card>
                <CardHeader>
                  <CardTitle>Review & Create</CardTitle>
                  <CardDescription>Review your vault configuration before creating</CardDescription>
                </CardHeader>
                <CardContent className="space-y-6">
                  <div>
                    <h3 className="font-semibold mb-2">Vault Settings</h3>
                    <div className="space-y-1 text-sm">
                      <p>
                        <span className="text-muted-foreground">Name:</span> {form.watch("name")}
                      </p>
                      <p>
                        <span className="text-muted-foreground">Check-In Interval:</span>{" "}
                        {form.watch("checkInIntervalDays")} days
                      </p>
                      <p>
                        <span className="text-muted-foreground">Grace Period:</span> {form.watch("gracePeriodDays")} days
                      </p>
                    </div>
                  </div>

                  <div>
                    <h3 className="font-semibold mb-2">Guardians ({form.watch("guardians").length})</h3>
                    <div className="space-y-2">
                      {form.watch("guardians").map((guardian, index) => (
                        <Card key={index}>
                          <CardContent className="pt-4">
                            <p className="font-medium">{guardian.name}</p>
                            <p className="text-sm text-muted-foreground">{guardian.email}</p>
                            {guardian.phone && <p className="text-sm text-muted-foreground">{guardian.phone}</p>}
                          </CardContent>
                        </Card>
                      ))}
                    </div>
                  </div>

                  <div>
                    <h3 className="font-semibold mb-2">Beneficiaries ({form.watch("beneficiaries").length})</h3>
                    <div className="space-y-2">
                      {form.watch("beneficiaries").map((beneficiary, index) => (
                        <Card key={index}>
                          <CardContent className="pt-4">
                            <p className="font-medium">{beneficiary.name}</p>
                            <p className="text-sm text-muted-foreground">{beneficiary.email}</p>
                            {beneficiary.phone && <p className="text-sm text-muted-foreground">{beneficiary.phone}</p>}
                          </CardContent>
                        </Card>
                      ))}
                    </div>
                  </div>

                  <div className="bg-muted p-4 rounded-md">
                    <p className="text-sm">
                      <strong>Note:</strong> Creating this vault will generate {form.watch("guardians").length} encrypted
                      key fragments using Shamir Secret Sharing (3-of-{Math.min(5, form.watch("guardians").length)}{" "}
                      threshold). Any 3 guardians can reconstruct your secret if needed.
                    </p>
                  </div>
                </CardContent>
              </Card>
            )}

            <div className="flex justify-between">
              <Button
                type="button"
                variant="outline"
                onClick={handleBack}
                disabled={currentStep === 1}
                data-testid="button-wizard-back"
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back
              </Button>

              {currentStep < STEPS.length ? (
                <Button type="button" onClick={handleNext} data-testid="button-wizard-next">
                  Next
                  <ArrowRight className="w-4 h-4 ml-2" />
                </Button>
              ) : (
                <Button type="submit" disabled={createVault.isPending} data-testid="button-create-vault">
                  {createVault.isPending ? "Creating..." : "Create Vault"}
                </Button>
              )}
            </div>
          </form>
        </Form>
      </div>
    </div>
  );
}

