import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { useVaults } from "@/hooks/useVaults";
import { useToast } from "@/hooks/use-toast";
import { ArrowLeft, Save, ExternalLink, CheckCircle, XCircle } from "lucide-react";
import { useSubscriptionStatus, useRemainingMonths, ESCROW_CONTRACT_ADDRESS } from "@/hooks/useSubscriptionEscrow";
import { useAccount } from "wagmi";
import { formatEther } from "viem";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";

export default function Settings() {
  const [, setLocation] = useLocation();
  const { data: vaultsData } = useVaults();
  const { toast } = useToast();
  const { address } = useAccount();

  const vault = vaultsData?.vaults?.[0];
  const vaultId = vault?.id;

  const { subscription, isLoading: subscriptionLoading } = useSubscriptionStatus(address);
  const { remainingMonths, isLoading: remainingLoading } = useRemainingMonths(address);

  const [emailEnabled, setEmailEnabled] = useState(true);
  const [smsEnabled, setSmsEnabled] = useState(false);
  const [telegramUsername, setTelegramUsername] = useState("");
  const [web3Address, setWeb3Address] = useState("");
  const [hardwareId, setHardwareId] = useState("");
  const [aiEnabled, setAiEnabled] = useState(false);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    if (!vault) return;
    // Hydrate from vault if these exist
    // @ts-expect-error dynamic fields
    setAiEnabled(Boolean(vault.aiSentinelEnabled));
    // @ts-expect-error dynamic fields
    const channels = vault.checkInChannels || {};
    setEmailEnabled(channels.email ?? true);
    setSmsEnabled(channels.sms ?? false);
    setTelegramUsername(channels.telegramUsername || "");
    setWeb3Address(channels.web3Address || "");
    setHardwareId(channels.hardwareId || "");
  }, [vault]);

  const handleSave = async () => {
    if (!vaultId) return;

    setSaving(true);
    try {
      const response = await fetch(`/api/vaults/${vaultId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          checkInChannels: {
            email: emailEnabled,
            sms: smsEnabled,
            telegramUsername: telegramUsername || undefined,
            web3Address: web3Address || undefined,
            hardwareId: hardwareId || undefined,
          },
          aiSentinelEnabled: aiEnabled,
        }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to save settings");
      }

      toast({
        title: "Settings Saved",
        description: "Your vault settings have been updated successfully.",
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to save settings",
        variant: "destructive",
      });
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="container max-w-4xl mx-auto py-8 px-4">
        <div className="mb-8">
          <Button variant="ghost" onClick={() => setLocation("/dashboard")} className="mb-4">
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Dashboard
          </Button>
          <h1 className="text-3xl font-bold mb-2">Vault Settings</h1>
          <p className="text-muted-foreground">Configure your vault preferences and check-in channels</p>
        </div>

        <div className="space-y-6">
          {/* Subscription Status */}
          <Card>
            <CardHeader>
              <CardTitle>Subscription Status</CardTitle>
              <CardDescription>
                Your on-chain subscription for GuardiaVault platform access
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {subscriptionLoading ? (
                <div className="space-y-3">
                  <Skeleton className="h-4 w-full" />
                  <Skeleton className="h-4 w-3/4" />
                  <Skeleton className="h-4 w-1/2" />
                </div>
              ) : subscription && subscription.active ? (
                <>
                  <div className="flex items-center justify-between">
                    <div className="space-y-0.5">
                      <Label>Status</Label>
                      <div className="flex items-center gap-2">
                        <Badge className="bg-green-600">
                          <CheckCircle className="w-3 h-3 mr-1" />
                          Active
                        </Badge>
                        {subscription.vaultTriggered && (
                          <Badge variant="secondary">Vault Triggered (+6 months)</Badge>
                        )}
                      </div>
                    </div>
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-1">
                      <Label className="text-muted-foreground">Monthly Rate</Label>
                      <p className="font-mono font-semibold">
                        {formatEther(subscription.monthlyRate)} ETH
                      </p>
                    </div>
                    <div className="space-y-1">
                      <Label className="text-muted-foreground">Remaining Months</Label>
                      <p className="font-semibold text-primary">
                        {remainingLoading ? "..." : remainingMonths || 0} months
                      </p>
                    </div>
                    <div className="space-y-1">
                      <Label className="text-muted-foreground">Prepaid Months</Label>
                      <p className="font-semibold">{subscription.prepaidMonths.toString()}</p>
                    </div>
                    <div className="space-y-1">
                      <Label className="text-muted-foreground">Months Consumed</Label>
                      <p className="font-semibold">{subscription.monthsConsumed.toString()}</p>
                    </div>
                  </div>

                  <div className="pt-4 border-t border-border space-y-2">
                    <div className="flex items-center justify-between">
                      <Label className="text-muted-foreground">Escrow Contract</Label>
                      <a
                        href={`https://etherscan.io/address/${ESCROW_CONTRACT_ADDRESS}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-xs font-mono text-primary hover:underline flex items-center gap-1"
                        data-testid="link-escrow-contract"
                      >
                        {ESCROW_CONTRACT_ADDRESS.slice(0, 6)}...{ESCROW_CONTRACT_ADDRESS.slice(-4)}
                        <ExternalLink className="w-3 h-3" />
                      </a>
                    </div>
                    <p className="text-xs text-muted-foreground">
                      Verify the escrow smart contract on Etherscan
                    </p>
                  </div>
                </>
              ) : (
                <div className="flex items-start gap-3 p-4 bg-muted rounded-lg">
                  <XCircle className="w-5 h-5 text-muted-foreground mt-0.5" />
                  <div className="space-y-2">
                    <p className="font-semibold">No Active Subscription</p>
                    <p className="text-sm text-muted-foreground">
                      You need an active subscription to create and maintain vaults. 
                      Create one when setting up a new vault.
                    </p>
                    <div className="pt-2">
                      <Label className="text-muted-foreground text-xs">Escrow Contract:</Label>
                      <a
                        href={`https://etherscan.io/address/${ESCROW_CONTRACT_ADDRESS}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-xs font-mono text-primary hover:underline flex items-center gap-1 mt-1"
                        data-testid="link-escrow-contract"
                      >
                        {ESCROW_CONTRACT_ADDRESS.slice(0, 6)}...{ESCROW_CONTRACT_ADDRESS.slice(-4)}
                        <ExternalLink className="w-3 h-3" />
                      </a>
                    </div>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Check-in Channels */}
          <Card>
            <CardHeader>
              <CardTitle>Check-In Channels</CardTitle>
              <CardDescription>
                Configure how you receive check-in reminders and notifications
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="flex items-center justify-between">
                <div className="space-y-0.5">
                  <Label htmlFor="email-checkin">Email Check-ins</Label>
                  <p className="text-sm text-muted-foreground">Receive check-in reminders via email</p>
                </div>
                <Switch
                  id="email-checkin"
                  checked={emailEnabled}
                  onCheckedChange={setEmailEnabled}
                />
              </div>

              <div className="flex items-center justify-between">
                <div className="space-y-0.5">
                  <Label htmlFor="sms-checkin">SMS Check-ins</Label>
                  <p className="text-sm text-muted-foreground">Receive check-in reminders via SMS</p>
                </div>
                <Switch
                  id="sms-checkin"
                  checked={smsEnabled}
                  onCheckedChange={setSmsEnabled}
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="telegram-username">Telegram Username (Optional)</Label>
                <Input
                  id="telegram-username"
                  placeholder="@your_username"
                  value={telegramUsername}
                  onChange={(e) => setTelegramUsername(e.target.value)}
                />
                <p className="text-sm text-muted-foreground">
                  Connect a Telegram bot for automated check-in notifications
                </p>
              </div>

              <div className="space-y-2">
                <Label htmlFor="web3-address">Web3 Signature Address (Optional)</Label>
                <Input
                  id="web3-address"
                  placeholder="0x..."
                  value={web3Address}
                  onChange={(e) => setWeb3Address(e.target.value)}
                />
                <p className="text-sm text-muted-foreground">
                  Use wallet signature for proof-of-life check-ins
                </p>
              </div>

              <div className="space-y-2">
                <Label htmlFor="hardware-id">Hardware Device ID (Optional)</Label>
                <Input
                  id="hardware-id"
                  placeholder="device-id"
                  value={hardwareId}
                  onChange={(e) => setHardwareId(e.target.value)}
                />
                <p className="text-sm text-muted-foreground">
                  Enable hardware ping-based keepalive detection
                </p>
              </div>
            </CardContent>
          </Card>

          {/* AI Sentinel */}
          <Card>
            <CardHeader>
              <CardTitle>GuardianX Sentinel (AI)</CardTitle>
              <CardDescription>
                Optional AI-verified identity and fraud detection to strengthen attestor signals
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="flex items-center justify-between">
                <div className="space-y-0.5">
                  <Label htmlFor="ai-sentinel">Enable AI Sentinel</Label>
                  <p className="text-sm text-muted-foreground">
                    Activate biometric liveness, deepfake detection, and transaction anomaly detection
                  </p>
                </div>
                <Switch
                  id="ai-sentinel"
                  checked={aiEnabled}
                  onCheckedChange={setAiEnabled}
                />
              </div>
            </CardContent>
          </Card>

          {/* Save Button */}
          <div className="flex justify-end">
            <Button onClick={handleSave} disabled={saving || !vaultId}>
              <Save className="w-4 h-4 mr-2" />
              {saving ? "Saving..." : "Save Settings"}
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
}

scripts/deploy-all.ts.content:
const hre = require("hardhat");
const fs = require("fs");
const path = require("path");
const { ethers } = hre;

/**
 * Deploy all GuardiaVault smart contracts
 * 
 * This script:
 * 1. Validates required environment variables
 * 2. Deploys SubscriptionEscrow (with deployer as initial platform address)
 * 3. Deploys GuardiaVault
 * 4. Automatically verifies both contracts on Etherscan (if API key is set)
 * 5. Saves combined deployment data to deployments/sepolia-all-contracts.json
 * 6. Displays summary table with addresses
 * 
 * Usage: npx hardhat run scripts/deploy-all.ts --network sepolia
 * Or use: npm run deploy:sepolia
 */
async function main() {
  console.log("ðŸš€ Starting full GuardiaVault platform deployment...\n");
  console.log("This will deploy:");
  console.log("  1. SubscriptionEscrow");
  console.log("  2. GuardiaVault\n");

  try {
    // Check required environment variables
    const network = await ethers.provider.getNetwork();
    const networkName = network.name === "unknown" ? "sepolia" : network.name;

    if (networkName === "sepolia") {
      if (!process.env.SEPOLIA_RPC_URL) {
        console.error("âŒ Error: SEPOLIA_RPC_URL not set in environment variables");
        console.log("Please add to your .env file:");
        console.log("SEPOLIA_RPC_URL=https://eth-sepolia.g.alchemy.com/v2/YOUR-API-KEY");
        process.exit(1);
      }

      if (!process.env.PRIVATE_KEY) {
        console.error("âŒ Error: PRIVATE_KEY not set in environment variables");
        console.log("Please add to your .env file:");
        console.log("PRIVATE_KEY=your_wallet_private_key");
        process.exit(1);
      }
    }

    // Get deployer account
    const [deployer] = await ethers.getSigners();
    console.log("ðŸ“‹ Deploying with account:", deployer.address);

    const balance = await ethers.provider.getBalance(deployer.address);
    console.log("ðŸ’° Account balance:", ethers.formatEther(balance), "ETH\n");

    if (balance === 0n) {
      console.error("âŒ Error: Deployer account has 0 ETH");
      console.log("Please fund your wallet with Sepolia ETH from a faucet:");
      console.log("- https://sepoliafaucet.com/");
      console.log("- https://www.alchemy.com/faucets/ethereum-sepolia");
      process.exit(1);
    }

    const chainId = network.chainId.toString();
    const deployments: any = {
      network: networkName,
      chainId: chainId,
      deployer: deployer.address,
      timestamp: new Date().toISOString(),
      contracts: {}
    };

    // ============================================================
    // 1. Deploy SubscriptionEscrow
    // ============================================================
    console.log("=".repeat(70));
    console.log("ðŸ“¦ STEP 1/2: Deploying SubscriptionEscrow");
    console.log("=".repeat(70));

    const platformAddress = deployer.address;
    console.log("Platform Address (initial):", platformAddress);
    console.log("(Set to deployer - update later via setPlatform() if needed)\n");

    console.log("ðŸš€ Deploying SubscriptionEscrow...");
    const SubscriptionEscrow = await ethers.getContractFactory("SubscriptionEscrow");
    const subscriptionEscrow = await SubscriptionEscrow.deploy(platformAddress);

    console.log("â³ Waiting for deployment...");
    await subscriptionEscrow.waitForDeployment();
    const escrowAddress = await subscriptionEscrow.getAddress();
    const escrowTx = subscriptionEscrow.deploymentTransaction();

    const escrowReceipt = await escrowTx?.wait();
    const escrowGasUsed = escrowReceipt?.gasUsed || 0n;

    console.log("âœ… SubscriptionEscrow deployed!");
    console.log("   Address:", escrowAddress);
    console.log("   TX Hash:", escrowTx?.hash);
    console.log("   Block:", escrowTx?.blockNumber);
    console.log("   Gas Used:", escrowGasUsed.toString());

    deployments.contracts.SubscriptionEscrow = {
      contractName: "SubscriptionEscrow",
      contractAddress: escrowAddress,
      transactionHash: escrowTx?.hash,
      blockNumber: escrowTx?.blockNumber,
      constructorArgs: {
        platformAddress: platformAddress
      },
      gasUsed: escrowGasUsed.toString(),
      verified: false
    };

    // ============================================================
    // 2. Deploy GuardiaVault
    // ============================================================
    console.log("\n" + "=".repeat(70));
    console.log("ðŸ“¦ STEP 2/2: Deploying GuardiaVault");
    console.log("=".repeat(70));

    console.log("ðŸš€ Deploying GuardiaVault...");
    const GuardiaVault = await ethers.getContractFactory("GuardiaVault");
    const guardiaVault = await GuardiaVault.deploy();

    console.log("â³ Waiting for deployment...");
    await guardiaVault.waitForDeployment();
    const vaultAddress = await guardiaVault.getAddress();
    const vaultTx = guardiaVault.deploymentTransaction();

    const vaultReceipt = await vaultTx?.wait();
    const vaultGasUsed = vaultReceipt?.gasUsed || 0n;

    console.log("âœ… GuardiaVault deployed!");
    console.log("   Address:", vaultAddress);
    console.log("   TX Hash:", vaultTx?.hash);
    console.log("   Block:", vaultTx?.blockNumber);
    console.log("   Gas Used:", vaultGasUsed.toString());

    deployments.contracts.GuardiaVault = {
      contractName: "GuardiaVault",
      contractAddress: vaultAddress,
      transactionHash: vaultTx?.hash,
      blockNumber: vaultTx?.blockNumber,
      constructorArgs: {},
      gasUsed: vaultGasUsed.toString(),
      verified: false
    };

    // ============================================================
    // Save deployment info
    // ============================================================
    const deploymentsDir = path.join(__dirname, "..", "deployments");
    if (!fs.existsSync(deploymentsDir)) {
      fs.mkdirSync(deploymentsDir, { recursive: true });
    }

    const deploymentFile = path.join(deploymentsDir, `${networkName}-all-contracts.json`);
    fs.writeFileSync(
      deploymentFile,
      JSON.stringify(deployments, null, 2),
      "utf8"
    );

    console.log("\nðŸ’¾ Combined deployment info saved to:", deploymentFile);

    // ============================================================
    // Verify contracts on Etherscan
    // ============================================================
    if (process.env.ETHERSCAN_API_KEY && networkName !== "hardhat" && networkName !== "localhost") {
      console.log("\n" + "=".repeat(70));
      console.log("ðŸ” Verifying contracts on Etherscan");
      console.log("=".repeat(70));
      console.log("â³ Waiting for 5 block confirmations...\n");

      try {
        // Verify SubscriptionEscrow
        console.log("1. Verifying SubscriptionEscrow...");
        await subscriptionEscrow.deploymentTransaction()?.wait(5);

        try {
          await hre.run("verify:verify", {
            address: escrowAddress,
            constructorArguments: [platformAddress],
          });
          deployments.contracts.SubscriptionEscrow.verified = true;
          console.log("   âœ… SubscriptionEscrow verified!");
        } catch (error: any) {
          if (error.message.includes("Already Verified")) {
            console.log("   â„¹ï¸  Already verified");
            deployments.contracts.SubscriptionEscrow.verified = true;
          } else {
            console.log("   âš ï¸  Verification failed:", error.message);
          }
        }

        // Verify GuardiaVault
        console.log("\n2. Verifying GuardiaVault...");
        await guardiaVault.deploymentTransaction()?.wait(5);

        try {
          await hre.run("verify:verify", {
            address: vaultAddress,
            constructorArguments: [],
          });
          deployments.contracts.GuardiaVault.verified = true;
          console.log("   âœ… GuardiaVault verified!");
        } catch (error: any) {
          if (error.message.includes("Already Verified")) {
            console.log("   â„¹ï¸  Already verified");
            deployments.contracts.GuardiaVault.verified = true;
          } else {
            console.log("   âš ï¸  Verification failed:", error.message);
          }
        }

        // Update deployment file with verification status
        fs.writeFileSync(
          deploymentFile,
          JSON.stringify(deployments, null, 2),
          "utf8"
        );

      } catch (error) {
        console.log("âš ï¸  Verification process encountered errors");
        console.log("\nYou can manually verify later:");
        console.log(`npx hardhat verify --network ${networkName} ${escrowAddress} "${platformAddress}"`);
        console.log(`npx hardhat verify --network ${networkName} ${vaultAddress}`);
      }
    } else if (!process.env.ETHERSCAN_API_KEY) {
      console.log("\nâ„¹ï¸  Skipping Etherscan verification (ETHERSCAN_API_KEY not set)");
    }

    // ============================================================
    // Print Summary Table
    // ============================================================
    console.log("\n" + "=".repeat(70));
    console.log("ðŸŽ‰ DEPLOYMENT COMPLETE - SUMMARY");
    console.log("=".repeat(70));
    console.log(`Network:          ${networkName}`);
    console.log(`Chain ID:         ${chainId}`);
    console.log(`Deployer:         ${deployer.address}`);
    console.log(`Timestamp:        ${deployments.timestamp}`);
    console.log("=".repeat(70));
    console.log("\nðŸ“‹ CONTRACT ADDRESSES:");
    console.log("=".repeat(70));

    const contracts = [
      {
        name: "SubscriptionEscrow",
        address: escrowAddress,
        verified: deployments.contracts.SubscriptionEscrow.verified ? "âœ…" : "â³"
      },
      {
        name: "GuardiaVault",
        address: vaultAddress,
        verified: deployments.contracts.GuardiaVault.verified ? "âœ…" : "â³"
      }
    ];

    contracts.forEach((contract, i) => {
      console.log(`${i + 1}. ${contract.name}`);
      console.log(`   Address:   ${contract.address}`);
      console.log(`   Verified:  ${contract.verified}`);
      if (networkName === "sepolia") {
        console.log(`   Etherscan: https://sepolia.etherscan.io/address/${contract.address}`);
      }
      console.log("");
    });

    console.log("=".repeat(70));

    // ============================================================
    // Frontend Configuration
    // ============================================================
    console.log("\nðŸ“‹ FRONTEND CONFIGURATION:");
    console.log("=".repeat(70));
    console.log("Add these to your .env file:\n");
    console.log(`VITE_ESCROW_CONTRACT_ADDRESS=${escrowAddress}`);
    console.log(`VITE_VAULT_CONTRACT_ADDRESS=${vaultAddress}`);
    console.log(`VITE_CHAIN_ID=${chainId}`);
    console.log(`VITE_NETWORK_NAME=${networkName}`);
    console.log("=".repeat(70));

    // ============================================================
    // Next Steps
    // ============================================================
    console.log("\nðŸ“ NEXT STEPS:");
    console.log("=".repeat(70));
    console.log("1. Update your frontend .env with the addresses above");
    console.log("\n2. Update platform address in SubscriptionEscrow (if needed):");
    console.log(`   - Current platform address: ${platformAddress}`);
    console.log("   - Call setPlatform() if you need a different address");
    console.log("\n3. Test the contracts:");
    console.log("   - Create a subscription in SubscriptionEscrow");
    console.log("   - Create a vault in GuardiaVault");
    console.log("   - Test check-ins and status transitions");
    console.log("\n4. Configure your frontend to interact with both contracts");
    console.log("=".repeat(70));

    console.log("\nâœ¨ Deployment successful! All contracts are ready to use.\n");

  } catch (error) {
    console.error("\nâŒ Deployment failed:");
    console.error(error);
    process.exitCode = 1;
  }
}

// Execute deployment
main().catch((error) => {
  console.error(error);
  process.exitCode = 1;
});

scripts/deploy-guardia-vault.ts.content:
const hre = require("hardhat");
const fs = require("fs");
const path = require("path");
const { ethers } = hre;

/**
 * Deploy GuardiaVault smart contract
 * 
 * This script:
 * 1. Validates required environment variables
 * 2. Deploys the GuardiaVault contract (no constructor args)
 * 3. Logs deployment information including actual gas used
 * 4. Automatically verifies on Etherscan (if API key is set)
 * 5. Saves deployment data to deployments/sepolia-guardia-vault.json
 * 
 * Usage: npx hardhat run scripts/deploy-guardia-vault.ts --network sepolia
 * Or use: npm run deploy:vault
 */
async function main() {
  console.log("ðŸš€ Starting GuardiaVault deployment...\n");

  try {
    // Check required environment variables
    const network = await ethers.provider.getNetwork();
    const networkName = network.name === "unknown" ? "sepolia" : network.name;

    if (networkName === "sepolia") {
      if (!process.env.SEPOLIA_RPC_URL) {
        console.error("âŒ Error: SEPOLIA_RPC_URL not set in environment variables");
        console.log("Please add to your .env file:");
        console.log("SEPOLIA_RPC_URL=https://eth-sepolia.g.alchemy.com/v2/YOUR-API-KEY");
        process.exit(1);
      }

      if (!process.env.PRIVATE_KEY) {
        console.error("âŒ Error: PRIVATE_KEY not set in environment variables");
        console.log("Please add to your .env file:");
        console.log("PRIVATE_KEY=your_wallet_private_key");
        process.exit(1);
      }
    }

    // Get deployer account
    const [deployer] = await ethers.getSigners();
    console.log("ðŸ“‹ Deploying with account:", deployer.address);

    const balance = await ethers.provider.getBalance(deployer.address);
    console.log("ðŸ’° Account balance:", ethers.formatEther(balance), "ETH");

    if (balance === 0n) {
      console.error("\nâŒ Error: Deployer account has 0 ETH");
      console.log("Please fund your wallet with Sepolia ETH from a faucet:");
      console.log("- https://sepoliafaucet.com/");
      console.log("- https://www.alchemy.com/faucets/ethereum-sepolia");
      process.exit(1);
    }

    console.log("\nðŸ“¦ Constructor Parameters:");
    console.log("   (None - GuardiaVault has no constructor arguments)\n");

    // Deploy contract
    console.log("ðŸš€ Deploying GuardiaVault...");
    const GuardiaVault = await ethers.getContractFactory("GuardiaVault");
    const guardiaVault = await GuardiaVault.deploy();

    console.log("â³ Waiting for deployment transaction...");
    await guardiaVault.waitForDeployment();
    const contractAddress = await guardiaVault.getAddress();
    const deployTx = guardiaVault.deploymentTransaction();

    const receipt = await deployTx?.wait();
    const gasUsed = receipt?.gasUsed || 0n;

    console.log("\nâœ… GuardiaVault deployed successfully!");
    console.log("ðŸ“ Contract address:", contractAddress);
    console.log("ðŸ”— Transaction hash:", deployTx?.hash);
    console.log("â›½ Gas used:", gasUsed.toString());

    if (deployTx?.blockNumber) {
      console.log("ðŸ“¦ Block number:", deployTx.blockNumber);
    }

    // Get network information
    const chainId = network.chainId.toString();

    // Prepare deployment data
    const deploymentInfo = {
      contractName: "GuardiaVault",
      contractAddress: contractAddress,
      network: networkName,
      chainId: chainId,
      deployer: deployer.address,
      transactionHash: deployTx?.hash,
      blockNumber: deployTx?.blockNumber,
      timestamp: new Date().toISOString(),
      constructorArgs: {},
      gasUsed: gasUsed.toString(),
      verified: false
    };

    // Save deployment info to network-specific file
    const deploymentsDir = path.join(__dirname, "..", "deployments");
    if (!fs.existsSync(deploymentsDir)) {
      fs.mkdirSync(deploymentsDir, { recursive: true });
    }

    const deploymentFile = path.join(deploymentsDir, `${networkName}-guardia-vault.json`);
    fs.writeFileSync(
      deploymentFile,
      JSON.stringify(deploymentInfo, null, 2),
      "utf8"
    );

    console.log("\nðŸ’¾ Deployment info saved to:", deploymentFile);

    // Verify on Etherscan if API key is set
    if (process.env.ETHERSCAN_API_KEY && networkName !== "hardhat" && networkName !== "localhost") {
      console.log("\nðŸ” Verifying contract on Etherscan...");
      console.log("â³ Waiting for 5 block confirmations...");

      try {
        // Wait for 5 confirmations
        await guardiaVault.deploymentTransaction()?.wait(5);

        console.log("â³ Running verification...");
        await hre.run("verify:verify", {
          address: contractAddress,
          constructorArguments: [],
        });

        deploymentInfo.verified = true;
        fs.writeFileSync(
          deploymentFile,
          JSON.stringify(deploymentInfo, null, 2),
          "utf8"
        );

        console.log("âœ… Contract verified on Etherscan!");
        console.log(`ðŸ”— View at: https://${networkName}.etherscan.io/address/${contractAddress}`);
      } catch (error: any) {
        if (error.message.includes("Already Verified")) {
          console.log("â„¹ï¸  Contract already verified on Etherscan");
          deploymentInfo.verified = true;
          fs.writeFileSync(
            deploymentFile,
            JSON.stringify(deploymentInfo, null, 2),
            "utf8"
          );
        } else {
          console.log("âš ï¸  Verification failed:", error.message);
          console.log("\nYou can manually verify later with:");
          console.log(`npx hardhat verify --network ${networkName} ${contractAddress}`);
        }
      }
    } else if (!process.env.ETHERSCAN_API_KEY) {
      console.log("\nâ„¹ï¸  Skipping Etherscan verification (ETHERSCAN_API_KEY not set)");
      console.log("To enable automatic verification, add to your .env file:");
      console.log("ETHERSCAN_API_KEY=your_etherscan_api_key");
    }

    // Print summary
    console.log("\n" + "=".repeat(70));
    console.log("ðŸŽ‰ DEPLOYMENT SUMMARY");
    console.log("=".repeat(70));
    console.log("Contract:           GuardiaVault");
    console.log("Network:            " + networkName);
    console.log("Chain ID:           " + chainId);
    console.log("Contract Address:   " + contractAddress);
    console.log("Deployer:           " + deployer.address);
    console.log("Transaction Hash:   " + deployTx?.hash);
    console.log("Block Number:       " + deployTx?.blockNumber);
    console.log("Verified:           " + (deploymentInfo.verified ? "Yes" : "No"));
    console.log("=".repeat(70));

    // Frontend configuration instructions
    console.log("\nðŸ“‹ FRONTEND CONFIGURATION:");
    console.log("=".repeat(70));
    console.log("Add to your .env file:");
    console.log(`VITE_VAULT_CONTRACT_ADDRESS=${contractAddress}`);
    console.log(`VITE_CHAIN_ID=${chainId}`);
    console.log("=".repeat(70));

    // Next steps
    console.log("\nðŸ“ NEXT STEPS:");
    console.log("=".repeat(70));
    console.log("1. Update frontend configuration with the contract address above");
    console.log("\n2. Test the deployment:");
    console.log("   - Create a vault with beneficiaries");
    console.log("   - Perform check-ins");
    console.log("   - Test vault status transitions");
    console.log("   - Add guardians and test attestations");
    console.log("\n3. Consider deploying SubscriptionEscrow:");
    console.log("   npm run deploy:escrow");
    console.log("=".repeat(70));

  } catch (error) {
    console.error("\nâŒ Deployment failed:");
    console.error(error);
    process.exitCode = 1;
  }
}

// Execute deployment
main().catch((error) => {
  console.error(error);
  process.exitCode = 1;
});

scripts/deploy-subscription-escrow.ts.content:
const hre = require("hardhat");
const fs = require("fs");
const path = require("path");
const { ethers } = hre;

/**
 * Deploy SubscriptionEscrow smart contract
 * 
 * This script:
 * 1. Validates required environment variables
 * 2. Deploys the SubscriptionEscrow contract
 * 3. Logs deployment information including actual gas used
 * 4. Automatically verifies on Etherscan (if API key is set)
 * 5. Saves deployment data to deployments/sepolia-subscription-escrow.json
 * 
 * Usage: npx hardhat run scripts/deploy-subscription-escrow.ts --network sepolia
 * Or use: npm run deploy:escrow
 */
async function main() {
  console.log("ðŸš€ Starting SubscriptionEscrow deployment...\n");

  try {
    // Check required environment variables
    const network = await ethers.provider.getNetwork();
    const networkName = network.name === "unknown" ? "sepolia" : network.name;

    if (networkName === "sepolia") {
      if (!process.env.SEPOLIA_RPC_URL) {
        console.error("âŒ Error: SEPOLIA_RPC_URL not set in environment variables");
        console.log("Please add to your .env file:");
        console.log("SEPOLIA_RPC_URL=https://eth-sepolia.g.alchemy.com/v2/YOUR-API-KEY");
        process.exit(1);
      }

      if (!process.env.PRIVATE_KEY) {
        console.error("âŒ Error: PRIVATE_KEY not set in environment variables");
        console.log("Please add to your .env file:");
        console.log("PRIVATE_KEY=your_wallet_private_key");
        process.exit(1);
      }
    }

    // Get deployer account
    const [deployer] = await ethers.getSigners();
    console.log("ðŸ“‹ Deploying with account:", deployer.address);

    const balance = await ethers.provider.getBalance(deployer.address);
    console.log("ðŸ’° Account balance:", ethers.formatEther(balance), "ETH");

    if (balance === 0n) {
      console.error("\nâŒ Error: Deployer account has 0 ETH");
      console.log("Please fund your wallet with Sepolia ETH from a faucet:");
      console.log("- https://sepoliafaucet.com/");
      console.log("- https://www.alchemy.com/faucets/ethereum-sepolia");
      process.exit(1);
    }

    // Platform address (initially set to deployer, can be updated later)
    const platformAddress = deployer.address;
    console.log("\nðŸ“¦ Constructor Parameters:");
    console.log("   Platform Address:", platformAddress);
    console.log("   (Set to deployer address initially - update via setPlatform() if needed)\n");

    // Deploy contract
    console.log("ðŸš€ Deploying SubscriptionEscrow...");
    const SubscriptionEscrow = await ethers.getContractFactory("SubscriptionEscrow");
    const subscriptionEscrow = await SubscriptionEscrow.deploy(platformAddress);

    console.log("â³ Waiting for deployment transaction...");
    await subscriptionEscrow.waitForDeployment();
    const contractAddress = await subscriptionEscrow.getAddress();
    const deployTx = subscriptionEscrow.deploymentTransaction();

    const receipt = await deployTx?.wait();
    const gasUsed = receipt?.gasUsed || 0n;

    console.log("\nâœ… SubscriptionEscrow deployed successfully!");
    console.log("ðŸ“ Contract address:", contractAddress);
    console.log("ðŸ”— Transaction hash:", deployTx?.hash);
    console.log("â›½ Gas used:", gasUsed.toString());

    if (deployTx?.blockNumber) {
      console.log("ðŸ“¦ Block number:", deployTx.blockNumber);
    }

    // Get network information
    const chainId = network.chainId.toString();

    // Prepare deployment data
    const deploymentInfo = {
      contractName: "SubscriptionEscrow",
      contractAddress: contractAddress,
      network: networkName,
      chainId: chainId,
      deployer: deployer.address,
      transactionHash: deployTx?.hash,
      blockNumber: deployTx?.blockNumber,
      timestamp: new Date().toISOString(),
      constructorArgs: {
        platformAddress: platformAddress
      },
      gasUsed: gasUsed.toString(),
      verified: false
    };

    // Save deployment info to network-specific file
    const deploymentsDir = path.join(__dirname, "..", "deployments");
    if (!fs.existsSync(deploymentsDir)) {
      fs.mkdirSync(deploymentsDir, { recursive: true });
    }

    const deploymentFile = path.join(deploymentsDir, `${networkName}-subscription-escrow.json`);
    fs.writeFileSync(
      deploymentFile,
      JSON.stringify(deploymentInfo, null, 2),
      "utf8"
    );

    console.log("\nðŸ’¾ Deployment info saved to:", deploymentFile);

    // Verify on Etherscan if API key is set
    if (process.env.ETHERSCAN_API_KEY && networkName !== "hardhat" && networkName !== "localhost") {
      console.log("\nðŸ” Verifying contract on Etherscan...");
      console.log("â³ Waiting for 5 block confirmations...");

      try {
        // Wait for 5 confirmations
        await subscriptionEscrow.deploymentTransaction()?.wait(5);

        console.log("â³ Running verification...");
        await hre.run("verify:verify", {
          address: contractAddress,
          constructorArguments: [platformAddress],
        });

        deploymentInfo.verified = true;
        fs.writeFileSync(
          deploymentFile,
          JSON.stringify(deploymentInfo, null, 2),
          "utf8"
        );

        console.log("âœ… Contract verified on Etherscan!");
        console.log(`ðŸ”— View at: https://${networkName}.etherscan.io/address/${contractAddress}`);
      } catch (error: any) {
        if (error.message.includes("Already Verified")) {
          console.log("â„¹ï¸  Contract already verified on Etherscan");
          deploymentInfo.verified = true;
          fs.writeFileSync(
            deploymentFile,
            JSON.stringify(deploymentInfo, null, 2),
            "utf8"
          );
        } else {
          console.log("âš ï¸  Verification failed:", error.message);
          console.log("\nYou can manually verify later with:");
          console.log(`npx hardhat verify --network ${networkName} ${contractAddress} "${platformAddress}"`);
        }
      }
    } else if (!process.env.ETHERSCAN_API_KEY) {
      console.log("\nâ„¹ï¸  Skipping Etherscan verification (ETHERSCAN_API_KEY not set)");
      console.log("To enable automatic verification, add to your .env file:");
      console.log("ETHERSCAN_API_KEY=your_etherscan_api_key");
    }

    // Print summary
    console.log("\n" + "=".repeat(70));
    console.log("ðŸŽ‰ DEPLOYMENT SUMMARY");
    console.log("=".repeat(70));
    console.log("Contract:           SubscriptionEscrow");
    console.log("Network:            " + networkName);
    console.log("Chain ID:           " + chainId);
    console.log("Contract Address:   " + contractAddress);
    console.log("Deployer:           " + deployer.address);
    console.log("Platform Address:   " + platformAddress);
    console.log("Transaction Hash:   " + deployTx?.hash);
    console.log("Block Number:       " + deployTx?.blockNumber);
    console.log("Verified:           " + (deploymentInfo.verified ? "Yes" : "No"));
    console.log("=".repeat(70));

    // Frontend configuration instructions
    console.log("\nðŸ“‹ FRONTEND CONFIGURATION:");
    console.log("=".repeat(70));
    console.log("Add to your .env file:");
    console.log(`VITE_ESCROW_CONTRACT_ADDRESS=${contractAddress}`);
    console.log(`VITE_CHAIN_ID=${chainId}`);
    console.log("=".repeat(70));

    // Next steps
    console.log("\nðŸ“ NEXT STEPS:");
    console.log("=".repeat(70));
    console.log("1. Update platform address if needed:");
    console.log(`   - Call setPlatform() with the actual platform address`);
    console.log("\n2. Deploy GuardiaVault contract:");
    console.log("   npm run deploy:vault");
    console.log("\n3. Test the deployment:");
    console.log("   - Create a subscription");
    console.log("   - Release monthly payments");
    console.log("   - Test vault triggering");
    console.log("=".repeat(70));

  } catch (error) {
    console.error("\nâŒ Deployment failed:");
    console.error(error);
    process.exitCode = 1;
  }
}

// Execute deployment
main().catch((error) => {
  console.error(error);
  process.exitCode = 1;
});

server/services/notifications.ts.content:
import fs from "fs/promises";
import path from "path";
import { storage } from "../storage";
import type { Notification, InsertNotification } from "@shared/schema";

const APP_URL = process.env.APP_URL || "http://localhost:5000";

export class NotificationService {
  private sendgridApiKey: string | null;
  private twilioAccountSid: string | null;
  private twilioAuthToken: string | null;
  private twilioPhoneNumber: string | null;
  private isDemoMode: boolean;

  constructor() {
    this.sendgridApiKey = process.env.SENDGRID_API_KEY || null;
    this.twilioAccountSid = process.env.TWILIO_ACCOUNT_SID || null;
    this.twilioAuthToken = process.env.TWILIO_AUTH_TOKEN || null;
    this.twilioPhoneNumber = process.env.TWILIO_PHONE_NUMBER || null;

    this.isDemoMode = !this.sendgridApiKey || !this.twilioAccountSid;

    if (this.isDemoMode) {
      console.log("âš ï¸  Notification Service: Running in DEMO MODE");
      console.log("   Missing credentials:");
      if (!this.sendgridApiKey) console.log("   - SENDGRID_API_KEY");
      if (!this.twilioAccountSid) console.log("   - TWILIO_ACCOUNT_SID");
      if (!this.twilioAuthToken) console.log("   - TWILIO_AUTH_TOKEN");
      if (!this.twilioPhoneNumber) console.log("   - TWILIO_PHONE_NUMBER");
    }
  }

  async sendEmail(
    to: string,
    subject: string,
    html: string,
    text: string
  ): Promise<{ success: boolean; error?: string; messageId?: string }> {
    if (!this.sendgridApiKey) {
      console.log(`ðŸ“§ DEMO MODE: Would send email to ${to}`);
      console.log(`   Subject: ${subject}`);
      return { success: true };
    }

    try {
      const sgMail = await import("@sendgrid/mail");
      sgMail.default.setApiKey(this.sendgridApiKey);

      const msg = {
        to,
        from: process.env.SENDGRID_FROM_EMAIL || "no-reply@guardiavault.com",
        subject,
        text,
        html,
      };

      const [response] = await sgMail.default.send(msg);

      console.log(`âœ… Email sent to ${to} - Status: ${response.statusCode}`);

      return {
        success: true,
        messageId: response.headers["x-message-id"] as string,
      };
    } catch (error: any) {
      console.error(`âŒ Email send failed to ${to}:`, error.message);

      if (error.response) {
        console.error("   SendGrid Error:", error.response.body);
      }

      return {
        success: false,
        error: error.message || "Unknown email send error",
      };
    }
  }

  async sendSMS(
    to: string,
    message: string
  ): Promise<{ success: boolean; error?: string; sid?: string }> {
    if (
      !this.twilioAccountSid ||
      !this.twilioAuthToken ||
      !this.twilioPhoneNumber
    ) {
      console.log(`ðŸ“± DEMO MODE: Would send SMS to ${to}`);
      console.log(`   Message: ${message.substring(0, 50)}...`);
      return { success: true };
    }

    try {
      const twilio = await import("twilio");
      const client = twilio.default(
        this.twilioAccountSid,
        this.twilioAuthToken
      );

      const twilioMessage = await client.messages.create({
        body: message,
        from: this.twilioPhoneNumber,
        to,
      });

      console.log(`âœ… SMS sent to ${to} - SID: ${twilioMessage.sid}`);

      return {
        success: true,
        sid: twilioMessage.sid,
      };
    } catch (error: any) {
      console.error(`âŒ SMS send failed to ${to}:`, error.message);

      return {
        success: false,
        error: error.message || "Unknown SMS send error",
      };
    }
  }

  async loadTemplate(
    templateName: string,
    variables: Record<string, string>
  ): Promise<{ html: string; text: string; subject: string }> {
    try {
      const templatesDir = path.join(process.cwd(), "server", "templates");

      const htmlPath = path.join(templatesDir, `${templateName}.html`);
      const textPath = path.join(templatesDir, `${templateName}.txt`);

      let html = await fs.readFile(htmlPath, "utf-8");
      let text = await fs.readFile(textPath, "utf-8");

      for (const [key, value] of Object.entries(variables)) {
        const regex = new RegExp(`{{${key}}}`, "g");
        html = html.replace(regex, value);
        text = text.replace(regex, value);
      }

      const subject = this.getSubjectForTemplate(templateName, variables);

      return { html, text, subject };
    } catch (error: any) {
      console.error(`Failed to load template ${templateName}:`, error.message);
      throw new Error(`Template loading failed: ${error.message}`);
    }
  }

  private getSubjectForTemplate(
    templateName: string,
    variables: Record<string, string>
  ): string {
    switch (templateName) {
      case "guardian-invitation":
        return `You've been named as a Guardian for ${variables.vaultName || "a vault"}`;
      case "beneficiary-notification":
        return `Important: Vault '${variables.vaultName || "Vault"}' has been triggered`;
      case "checkin-reminder":
        return `Reminder: Vault Check-In Due in ${variables.daysRemaining || "X"} days`;
      case "checkin-warning":
        return `URGENT: Final Warning - Vault Check-In Overdue`;
      default:
        return "GuardiaVault Notification";
    }
  }

  async processNotification(
    notificationId: string
  ): Promise<{ success: boolean; error?: string }> {
    try {
      const notification = await storage.getNotification(notificationId);

      if (!notification) {
        return { success: false, error: "Notification not found" };
      }

      if (notification.status !== "pending") {
        console.log(`â­ï¸  Skipping notification ${notificationId} - Status: ${notification.status}`);
        return { success: true };
      }

      console.log(`ðŸ“¤ Processing notification ${notificationId} (${notification.type})`);

      let result: { success: boolean; error?: string };

      if (notification.channel === "email") {
        const templateData = JSON.parse(notification.message);
        const { html, text, subject } = await this.loadTemplate(
          this.getTemplateNameForType(notification.type),
          templateData
        );

        result = await this.sendEmail(
          notification.recipient,
          subject,
          html,
          text
        );
      } else if (notification.channel === "sms") {
        result = await this.sendSMS(
          notification.recipient,
          notification.message
        );
      } else {
        return {
          success: false,
          error: `Unsupported channel: ${notification.channel}`,
        };
      }

      if (result.success) {
        await storage.updateNotification(notificationId, {
          status: "sent",
          sentAt: new Date(),
        });
        console.log(`âœ… Notification ${notificationId} sent successfully`);
      } else {
        const attempts = (notification as any).attempts || 0;
        const newAttempts = attempts + 1;

        if (newAttempts >= 3) {
          await storage.updateNotification(notificationId, {
            status: "failed",
            message: `${notification.message}\n\nError: ${result.error}`,
          });
          console.error(
            `âŒ Notification ${notificationId} failed after ${newAttempts} attempts`
          );
        } else {
          console.warn(
            `âš ï¸  Notification ${notificationId} failed (attempt ${newAttempts}/3) - will retry`
          );
        }
      }

      return result;
    } catch (error: any) {
      console.error(
        `âŒ Error processing notification ${notificationId}:`,
        error.message
      );
      return { success: false, error: error.message };
    }
  }

  async processPendingNotifications(): Promise<{
    processed: number;
    sent: number;
    failed: number;
  }> {
    try {
      const pending = await storage.getPendingNotifications();

      console.log(`ðŸ”„ Processing ${pending.length} pending notifications`);

      let sent = 0;
      let failed = 0;

      for (const notification of pending) {
        const result = await this.processNotification(notification.id);
        if (result.success) {
          sent++;
        } else {
          failed++;
        }
      }

      console.log(`âœ… Processed ${pending.length} notifications: ${sent} sent, ${failed} failed`);

      return {
        processed: pending.length,
        sent,
        failed,
      };
    } catch (error: any) {
      console.error("âŒ Error processing pending notifications:", error.message);
      return {
        processed: 0,
        sent: 0,
        failed: 0,
      };
    }
  }

  private getTemplateNameForType(type: string): string {
    switch (type) {
      case "guardian_invitation":
        return "guardian-invitation";
      case "beneficiary_notification":
        return "beneficiary-notification";
      case "check_in_reminder":
        return "checkin-reminder";
      case "check_in_warning":
      case "check_in_critical":
        return "checkin-warning";
      default:
        throw new Error(`Unknown notification type: ${type}`);
    }
  }

  async createGuardianInvitation(params: {
    vaultId: string;
    guardianId: string;
    guardianEmail: string;
    vaultName: string;
    ownerName: string;
    inviteToken: string;
  }): Promise<Notification> {
    const { vaultId, guardianEmail, vaultName, ownerName, inviteToken } =
      params;

    const acceptLink = `${APP_URL}/accept-invite?token=${inviteToken}&role=guardian`;

    const templateData = {
      vaultName,
      ownerName,
      acceptLink,
    };

    const notification: InsertNotification = {
      vaultId,
      type: "guardian_invitation",
      recipient: guardianEmail,
      channel: "email",
      message: JSON.stringify(templateData),
    };

    return await storage.createNotification(notification);
  }

  async createBeneficiaryNotification(params: {
    vaultId: string;
    beneficiaryEmail: string;
    vaultName: string;
  }): Promise<Notification> {
    const { vaultId, beneficiaryEmail, vaultName } = params;

    const claimLink = `${APP_URL}/claims?vault=${vaultId}`;

    const templateData = {
      vaultName,
      claimLink,
    };

    const notification: InsertNotification = {
      vaultId,
      type: "beneficiary_notification",
      recipient: beneficiaryEmail,
      channel: "email",
      message: JSON.stringify(templateData),
    };

    return await storage.createNotification(notification);
  }

  async createCheckInReminder(params: {
    vaultId: string;
    ownerEmail: string;
    ownerName: string;
    vaultName: string;
    daysRemaining: number;
    dueDate: string;
    gracePeriod: number;
  }): Promise<Notification> {
    const {
      vaultId,
      ownerEmail,
      ownerName,
      vaultName,
      daysRemaining,
      dueDate,
      gracePeriod,
    } = params;

    const checkInLink = `${APP_URL}/checkins`;

    const templateData = {
      ownerName,
      vaultName,
      daysRemaining: daysRemaining.toString(),
      dueDate,
      gracePeriod: gracePeriod.toString(),
      checkInLink,
    };

    const notification: InsertNotification = {
      vaultId,
      type: "check_in_reminder",
      recipient: ownerEmail,
      channel: "email",
      message: JSON.stringify(templateData),
    };

    return await storage.createNotification(notification);
  }

  async createCheckInWarning(params: {
    vaultId: string;
    ownerEmail: string;
    ownerName: string;
    vaultName: string;
    hoursRemaining: number;
    triggerDate: string;
  }): Promise<Notification> {
    const {
      vaultId,
      ownerEmail,
      ownerName,
      vaultName,
      hoursRemaining,
      triggerDate,
    } = params;

    const checkInLink = `${APP_URL}/checkins`;

    const templateData = {
      ownerName,
      vaultName,
      hoursRemaining: hoursRemaining.toString(),
      triggerDate,
      checkInLink,
    };

    const notification: InsertNotification = {
      vaultId,
      type: "check_in_critical",
      recipient: ownerEmail,
      channel: "email",
      message: JSON.stringify(templateData),
    };

    return await storage.createNotification(notification);
  }
}

export const notificationService = new NotificationService();

export async function sendGuardianNotificationLink(params: {
  guardian: {
    id: string;
    name: string;
    email: string | null;
    phone: string | null;
    telegram: string | null;
  };
  url: string;
  vaultName: string;
}): Promise<{ ok: boolean; error?: string }> {
  const { guardian, url, vaultName } = params;

  try {
    let result: { success: boolean; error?: string } | null = null;

    if (guardian.email) {
      const message = `Hello ${guardian.name},\n\nYou have been requested to take action for vault "${vaultName}".\n\nPlease visit: ${url}\n\nThank you,\nGuardiaVault Team`;

      result = await notificationService.sendEmail(
        guardian.email,
        `Action Required: ${vaultName}`,
        `<p>Hello ${guardian.name},</p><p>You have been requested to take action for vault "<strong>${vaultName}</strong>".</p><p><a href="${url}">Click here to take action</a></p><p>Thank you,<br>GuardiaVault Team</p>`,
        message
      );
    } else if (guardian.phone) {
      const message = `GuardiaVault: Action required for vault "${vaultName}". Visit: ${url}`;
      result = await notificationService.sendSMS(guardian.phone, message);
    } else {
      console.warn(`Guardian ${guardian.id} has no email or phone configured`);
      return { ok: false, error: "No contact method available" };
    }

    return { ok: result?.success ?? false, error: result?.error };
  } catch (error: any) {
    console.error(`Failed to send guardian notification to ${guardian.id}:`, error.message);
    return { ok: false, error: error.message };
  }
}

server/jobs/notification-processor.ts.content:
import { notificationService } from "../services/notifications";

let processorInterval: NodeJS.Timeout | null = null;
const PROCESS_INTERVAL_MS = 5 * 60 * 1000; // 5 minutes

export function startNotificationProcessor() {
  if (processorInterval) {
    console.log("âš ï¸  Notification processor already running");
    return;
  }

  console.log("ðŸš€ Starting notification processor (interval: 5 minutes)");

  async function processNotifications() {
    try {
      const stats = await notificationService.processPendingNotifications();

      if (stats.processed > 0) {
        console.log(
          `ðŸ“Š Notification processing complete: ${stats.sent} sent, ${stats.failed} failed, ${stats.processed} total`
        );
      }
    } catch (error: any) {
      console.error("âŒ Error in notification processor:", error.message);
    }
  }

  processNotifications();

  processorInterval = setInterval(processNotifications, PROCESS_INTERVAL_MS);

  console.log("âœ… Notification processor started successfully");
}

export function stopNotificationProcessor() {
  if (processorInterval) {
    clearInterval(processorInterval);
    processorInterval = null;
    console.log("ðŸ›‘ Notification processor stopped");
  }
}
